  // Send broadcast
  async sendBroadcast(e) {
    e.preventDefault();

    const title = document.getElementById("broadcast-title").value;
    const message = document.getElementById("broadcast-message").value;
    const recipients = document.getElementById("broadcast-recipients").value;

    if (!title || !message) {
      showToast("Please fill in all fields", "warning");
      return;
    }

    try {
      let targetUsers = [];

      if (recipients === "all") {
        const { data: users, error } = await supabase
          .from("profiles")
          .select("id, email");
        if (error) throw error;
        targetUsers = users;
      } else if (recipients === "select") {
        const checkboxes = document.querySelectorAll(
          '#user-checkboxes input[type="checkbox"]:checked'
        );
        targetUsers = Array.from(checkboxes).map((cb) => ({
          id: cb.value,
          email: cb.dataset.email,
        }));
      }

      if (targetUsers.length === 0) {
        showToast("No recipients selected", "warning");
        return;
      }

      // Send broadcast to each user
      for (const user of targetUsers) {
        await supabase.from("notifications").insert({
          user_id: user.id,
          title: title,
          message: message,
          type: "broadcast",
          read: false,
        });
      }

      // Log activity
      await this.logActivity(
        "Broadcast Sent",
        `Sent broadcast "${title}" to ${targetUsers.length} users`
      );

      showToast(`Broadcast sent to ${targetUsers.length} users`, "success");
      document.getElementById("broadcast-title").value = "";
      document.getElementById("broadcast-message").value = "";
    } catch (error) {
      console.error("Error sending broadcast:", error);
      showToast("Error sending broadcast", "error");
    }
  },

