                        <div class="detail-row">
                            <label>Processed By:</label>
                            <span>${withdrawal.processed_by}</span>
                        </div>
                        `
                            : ""
                        }
                        ${
                          withdrawal.notes
                            ? `
                        <div class="detail-row">
                            <label>Notes:</label>
                            <span>${withdrawal.notes}</span>
                        </div>
                        `
                            : ""
                        }
                    </div>
                </div>
                <div class="modal-footer">
                    ${
                      withdrawal.status === "pending"
                        ? `
                        <button class="btn btn-success" onclick="admin.approveWithdrawal('${withdrawal.id}')">Approve</button>
                        <button class="btn btn-danger" onclick="admin.rejectWithdrawal('${withdrawal.id}')">Reject</button>
                    `
                        : ""
                    }
                    <button class="btn btn-secondary" onclick="closeModal('view-withdrawal-modal')">Close</button>
                </div>
            `;

      // Update or create modal
      let modal = document.getElementById("view-withdrawal-modal");
      if (!modal) {
        modal = document.createElement("div");
        modal.id = "view-withdrawal-modal";
        modal.className = "modal";
        document.body.appendChild(modal);
      }
      modal.innerHTML = modalContent;
      openModal("view-withdrawal-modal");
    } catch (error) {
      console.error("Error viewing withdrawal:", error);
      showToast("Error loading withdrawal details", "error");
    }
  },

  // Approve withdrawal
  async approveWithdrawal(withdrawalId) {
    // Delegate to primary admin implementation if present
    try {
      if (
        window.admin &&
        window.admin !== this &&
        typeof window.admin.approveWithdrawal === "function"
      ) {
        await window.admin.approveWithdrawal(withdrawalId);
        if (typeof this.loadWithdrawals === "function") this.loadWithdrawals();
        return;
      }

      const { error } = await supabase
        .from("withdrawals")
        .update({ status: "completed" })
        .eq("id", withdrawalId);

      if (error) throw error;

      // Log activity
      await this.logActivity(
        "Withdrawal Approved",
        `Approved withdrawal ${withdrawalId}`
      );

      showToast("Withdrawal approved successfully", "success");
      this.loadWithdrawals();
    } catch (error) {
      console.error("Error approving withdrawal:", error);
      showToast("Error approving withdrawal", "error");
    }
  },

  // Reject withdrawal
  async rejectWithdrawal(withdrawalId) {
    try {
      if (
        window.admin &&
        window.admin !== this &&
        typeof window.admin.rejectWithdrawal === "function"
      ) {
        await window.admin.rejectWithdrawal(withdrawalId);
        if (typeof this.loadWithdrawals === "function") this.loadWithdrawals();
        return;
      }

      const { error } = await supabase
        .from("withdrawals")
        .update({ status: "rejected" })
        .eq("id", withdrawalId);

      if (error) throw error;

      // Log activity
      await this.logActivity(
        "Withdrawal Rejected",
        `Rejected withdrawal ${withdrawalId}`
      );

      showToast("Withdrawal rejected successfully", "success");
      this.loadWithdrawals();
    } catch (error) {
      console.error("Error rejecting withdrawal:", error);
      showToast("Error rejecting withdrawal", "error");
    }
  },

  // View trade
  async viewTrade(tradeId) {
    try {
      const { data: trade, error } = await supabase
        .from("trades")
        .select(
          `
                    *,
                    profiles:profiles(full_name, email)
                `
        )
        .eq("id", tradeId)
        .single();

      if (error) throw error;

      // Create view trade modal content
      const modalContent = `
                <div class="modal-header">
                    <h3>Trade Details</h3>
                    <button class="close-btn" onclick="closeModal('view-trade-modal')">&times;</button>
                </div>
                <div class="modal-body">
                    <div class="trade-details">
                        <div class="detail-row">
                            <label>Trade ID:</label>
                            <span>${trade.id}</span>
                        </div>
                        <div class="detail-row">
                            <label>User:</label>
                            <span>${
                              trade.profiles?.full_name ||
                              trade.profiles?.email ||
                              "N/A"
                            }</span>
                        </div>
                        <div class="detail-row">
                            <label>Type:</label>
                            <span class="status ${trade.type}">${
        trade.type
      }</span>
                        </div>
                        <div class="detail-row">
                            <label>Amount:</label>
                            <span>$${trade.amount?.toFixed(2) || "0.00"}</span>
                        </div>
                        <div class="detail-row">
                            <label>Entry Price:</label>
                            <span>$${
                              trade.entry_price?.toFixed(5) || "N/A"
                            }</span>
                        </div>
                        <div class="detail-row">
                            <label>Current Price:</label>
                            <span>$${
                              trade.current_price?.toFixed(5) || "N/A"
                            }</span>
                        </div>
                        <div class="detail-row">
                            <label>Stop Loss:</label>
                            <span>$${
                              trade.stop_loss?.toFixed(5) || "N/A"
                            }</span>
                        </div>
                        <div class="detail-row">
                            <label>Take Profit:</label>
                            <span>$${
                              trade.take_profit?.toFixed(5) || "N/A"
                            }</span>
                        </div>
                        <div class="detail-row">
                            <label>Profit/Loss:</label>
                            <span class="status ${
                              trade.profit_loss >= 0 ? "profit" : "loss"
                            }">$${
        trade.profit_loss?.toFixed(2) || "0.00"
      }</span>
                        </div>
                        <div class="detail-row">
                            <label>Status:</label>
                            <span class="status ${trade.status}">${
        trade.status
      }</span>
                        </div>
                        <div class="detail-row">
                            <label>Symbol:</label>
                            <span>${trade.symbol || "N/A"}</span>
                        </div>
                        <div class="detail-row">
                            <label>Leverage:</label>
                            <span>${trade.leverage || 1}x</span>
                        </div>
                        <div class="detail-row">
                            <label>Opened:</label>
                            <span>${this.formatDate(trade.created_at)}</span>
                        </div>
                        ${
                          trade.closed_at
                            ? `
                        <div class="detail-row">
                            <label>Closed:</label>
                            <span>${this.formatDate(trade.closed_at)}</span>
                        </div>
                        `
                            : ""
                        }
                        ${
                          trade.result
                            ? `
                        <div class="detail-row">
                            <label>Result:</label>
                            <span class="status ${trade.result}">${trade.result}</span>
                        </div>
                        `
                            : ""
                        }
                        ${
                          trade.duration
                            ? `
                        <div class="detail-row">
                            <label>Duration:</label>
                            <span>${trade.duration}</span>
                        </div>
                        `
                            : ""
                        }
                        ${
                          trade.notes
                            ? `
                        <div class="detail-row">
                            <label>Notes:</label>
                            <span>${trade.notes}</span>
                        </div>
                        `
                            : ""
                        }
                    </div>
                </div>
                <div class="modal-footer">
                    ${
                      trade.status === "open"
                        ? `
                        <button class="btn btn-success" onclick="admin.closeTrade('${trade.id}', 'profit')">Mark as Win</button>
                        <button class="btn btn-danger" onclick="admin.closeTrade('${trade.id}', 'loss')">Mark as Loss</button>
                    `
                        : ""
                    }
                    <button class="btn btn-secondary" onclick="closeModal('view-trade-modal')">Close</button>
                </div>
            `;

      // Update or create modal
      let modal = document.getElementById("view-trade-modal");
      if (!modal) {
        modal = document.createElement("div");
        modal.id = "view-trade-modal";
        modal.className = "modal";
        document.body.appendChild(modal);
      }
      modal.innerHTML = modalContent;
      openModal("view-trade-modal");
    } catch (error) {
      console.error("Error viewing trade:", error);
      showToast("Error loading trade details", "error");
    }
  },

  // Close trade
  async closeTrade(tradeId, result) {
    try {
      if (
        window.admin &&
        window.admin !== this &&
        typeof window.admin.closeTrade === "function"
      ) {
        await window.admin.closeTrade(
          tradeId,
          result === "profit" ? "profit" : "loss"
        );
        if (typeof this.loadTrades === "function") this.loadTrades();
        return;
      }

      const { error } = await supabase
        .from("trades")
        .update({
          status: "closed",
          closed_at: new Date().toISOString(),
          result: result,
        })
        .eq("id", tradeId);

      if (error) throw error;

      // Log activity
      await this.logActivity(
        "Trade Closed",
        `Closed trade ${tradeId} with result: ${result}`
      );

      showToast(`Trade closed as ${result}`, "success");
      this.loadTrades();
    } catch (error) {
      console.error("Error closing trade:", error);
      showToast("Error closing trade", "error");
    }
  },

  // Export transactions
  async exportTransactions() {
    try {
      const { data: transactions, error } = await supabase
        .from("transactions")
        .select(
          `
                    *,
                    profiles:profiles(full_name, email)
                `
        )
        .order("created_at", { ascending: false });

      if (error) throw error;

      // Create CSV content
      const csvContent = [
        [
          "ID",
          "User",
          "Type",
          "Amount",
          "Status",
          "Payment Method",
          "Reference",
          "Created",
          "Updated",
        ].join(","),
        ...transactions.map((t) =>
          [
            t.id,
            t.profiles?.full_name || t.profiles?.email || "N/A",
            t.type,
            t.amount || 0,
            t.status || "pending",
            t.payment_method || "N/A",
            t.reference || "N/A",
            this.formatDate(t.created_at),
            t.updated_at ? this.formatDate(t.updated_at) : "N/A",
          ].join(",")
        ),
      ].join("\n");

      // Download CSV file
      this.downloadCSV(
        csvContent,
        `transactions_export_${new Date().toISOString().split("T")[0]}.csv`
      );
      showToast("Transactions exported successfully", "success");
    } catch (error) {
      console.error("Error exporting transactions:", error);
      showToast("Error exporting transactions", "error");
    }
  },

  // Export withdrawals
  async exportWithdrawals() {
    try {
      const { data: withdrawals, error } = await supabase
        .from("withdrawals")
        .select(
          `
                    *,
                    profiles:profiles(full_name, email)
                `
        )
        .order("created_at", { ascending: false });

      if (error) throw error;

      // Create CSV content
      const csvContent = [
        [
          "ID",
          "User",
          "Amount",
          "Method",
          "Status",
          "Bank Name",
          "Account Number",
          "Routing Number",
          "SWIFT",
          "Requested",
          "Processed",
        ].join(","),
        ...withdrawals.map((w) =>
          [
            w.id,
            w.profiles?.full_name || w.profiles?.email || "N/A",
            w.amount || 0,
            w.method || "bank",
            w.status || "pending",
            w.bank_name || "N/A",
            w.account_number ? "****" + w.account_number.slice(-4) : "N/A",
            w.routing_number || "N/A",
            w.swift_code || "N/A",
            this.formatDate(w.created_at),
            w.processed_at ? this.formatDate(w.processed_at) : "N/A",
          ].join(",")
        ),
      ].join("\n");

      // Download CSV file
      this.downloadCSV(
        csvContent,
        `withdrawals_export_${new Date().toISOString().split("T")[0]}.csv`
      );
      showToast("Withdrawals exported successfully", "success");
    } catch (error) {
      console.error("Error exporting withdrawals:", error);
      showToast("Error exporting withdrawals", "error");
    }
  },

  // Export trades
  async exportTrades() {
    try {
      const { data: trades, error } = await supabase
        .from("trades")
        .select(
          `
                    *,
                    profiles:profiles(full_name, email)
                `
        )
        .order("created_at", { ascending: false });

      if (error) throw error;

      // Create CSV content
      const csvContent = [
        [
          "ID",
          "User",
          "Type",
          "Amount",
          "Symbol",
          "Entry Price",
          "Current Price",
          "Stop Loss",
          "Take Profit",
          "P&L",
          "Status",
          "Leverage",
          "Opened",
          "Closed",
          "Result",
        ].join(","),
        ...trades.map((t) =>
          [
            t.id,
            t.profiles?.full_name || t.profiles?.email || "N/A",
            t.type,
            t.amount || 0,
            t.symbol || "N/A",
            t.entry_price || "N/A",
            t.current_price || "N/A",
            t.stop_loss || "N/A",
            t.take_profit || "N/A",
            t.profit_loss || 0,
            t.status,
            t.leverage || 1,
            this.formatDate(t.created_at),
            t.closed_at ? this.formatDate(t.closed_at) : "N/A",
            t.result || "N/A",
          ].join(",")
        ),
      ].join("\n");

      // Download CSV file
      this.downloadCSV(
        csvContent,
        `trades_export_${new Date().toISOString().split("T")[0]}.csv`
      );
      showToast("Trades exported successfully", "success");
    } catch (error) {
      console.error("Error exporting trades:", error);
      showToast("Error exporting trades", "error");
    }
  },

  // Export logs
  async exportLogs() {
    try {
      const { data: logs, error } = await supabase
        .from("activity_logs")
        .select(
          `
                    *,
                    profiles:profiles(full_name, email)
                `
        )
        .order("created_at", { ascending: false });

      if (error) throw error;

      // Create CSV content
      const csvContent = [
        ["ID", "User", "Action", "Details", "Created"].join(","),
        ...logs.map((l) =>
          [
            l.id,
            l.profiles?.full_name || l.profiles?.email || "System",
            l.action,
            l.details || "N/A",
            this.formatDate(l.created_at),
          ].join(",")
        ),
      ].join("\n");

      // Download CSV file
      this.downloadCSV(
        csvContent,
        `activity_logs_export_${new Date().toISOString().split("T")[0]}.csv`
      );
      showToast("Activity logs exported successfully", "success");
    } catch (error) {
      console.error("Error exporting logs:", error);
      showToast("Error exporting logs", "error");
    }
  },

  // Helper function to download CSV
  downloadCSV(csvContent, filename) {
    const blob = new Blob([csvContent], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  },

  // Log activity
  async logActivity(action, details) {
    try {
      await supabase.from("activity_logs").insert({
        user_id: this.currentUser.id,
        action: action,
        details: details,
        created_at: new Date().toISOString(),
      });
    } catch (error) {
      console.error("Error logging activity:", error);
    }
  },

  // Get activity icon
  getActivityIcon(action) {
    const iconMap = {
      "User Updated": "user-edit",
      "User Deleted": "user-times",
      "Transaction Approved": "check-circle",
      "Transaction Rejected": "times-circle",
      "Withdrawal Approved": "check-circle",
      "Withdrawal Rejected": "times-circle",
      "Trade Closed": "chart-line",
      "Broadcast Sent": "bullhorn",
      Login: "sign-in-alt",
      Logout: "sign-out-alt",
    };

    return iconMap[action] || "info-circle";
  },

  // Format date
  formatDate(dateString) {
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  },

  // Format time ago
  formatTimeAgo(dateString) {
    const date = new Date(dateString);
    const now = new Date();
    const diffInSeconds = Math.floor((now - date) / 1000);

    if (diffInSeconds < 60) return "Just now";
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)}m ago`;
    if (diffInSeconds < 86400)
      return `${Math.floor(diffInSeconds / 3600)}h ago`;
    if (diffInSeconds < 604800)
      return `${Math.floor(diffInSeconds / 86400)}d ago`;

    return this.formatDate(dateString);
  },

  // Close all modals
  closeAllModals() {
    document.querySelectorAll(".modal").forEach((modal) => {
      modal.classList.remove("active");
    });
  },

  // Refresh dashboard data
  async refreshData() {
    try {
      this.isLoading = true;
      showToast("Refreshing data...", "info");

      // Reload all dashboard data
      await this.loadDashboardData();
      await this.loadUsers();
      await this.loadTransactions();
      await this.loadWithdrawals();
      await this.loadTrades();
      await this.loadLogs();

      showToast("Data refreshed successfully", "success");
    } catch (error) {
      console.error("Error refreshing data:", error);
      showToast("Error refreshing data", "error");
    } finally {
      this.isLoading = false;
    }
  },

  // Process withdrawal (approve/reject)
  async processWithdrawal(action) {
    const modal = document.getElementById("withdrawal-modal");
    const withdrawalId = modal.dataset.withdrawalId;

    if (!withdrawalId) {
      showToast("No withdrawal selected", "error");
      return;
    }

    try {
      const { error } = await supabase
        .from("withdrawals")
        .update({
          status: action === "approve" ? "completed" : "rejected",
          processed_at: new Date().toISOString(),
          processed_by: this.currentUser.id,
        })
        .eq("id", withdrawalId);

      if (error) throw error;

      // Log activity
      await this.logActivity(
        `Withdrawal ${action === "approve" ? "Approved" : "Rejected"}`,
        `${
          action === "approve" ? "Approved" : "Rejected"
        } withdrawal ${withdrawalId}`
      );

      showToast(
        `Withdrawal ${
          action === "approve" ? "approved" : "rejected"
        } successfully`,
        "success"
      );
      closeModal("withdrawal-modal");
      this.loadWithdrawals();
      this.loadDashboardData(); // Refresh dashboard stats
    } catch (error) {
      console.error("Error processing withdrawal:", error);
      showToast("Error processing withdrawal", "error");
    }
  },

  // Logout admin
  async logoutAdmin() {
    try {
      // Log activity
      await this.logActivity("Admin Logout", "Admin logged out");

      // Sign out from Supabase
      await supabase.auth.signOut();

      // Redirect to login page
      window.location.href = "index.html";
    } catch (error) {
      console.error("Error during logout:", error);
      // Force redirect even if logout fails
      window.location.href = "index.html";
    }
  },

  // Export users
  async exportUsers() {
    try {
      const { data: users, error } = await supabase
        .from("profiles")
        .select("*")
        .order("created_at", { ascending: false });

      if (error) throw error;

      // Create CSV content
      const csvContent = [
        ["Name", "Email", "Balance", "Status", "Joined", "Last Login"].join(
          ","
        ),
        ...users.map((user) =>
          [
            user.full_name || "",
            user.email,
            user.balance || 0,
            user.status || "active",
            this.formatDate(user.created_at),
            user.last_login ? this.formatDate(user.last_login) : "Never",
          ].join(",")
        ),
      ].join("\n");

      // Download CSV file
      const blob = new Blob([csvContent], { type: "text/csv" });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `users_export_${new Date().toISOString().split("T")[0]}.csv`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

      showToast("Users exported successfully", "success");
    } catch (error) {
      console.error("Error exporting users:", error);
      showToast("Error exporting users", "error");
    }
  },
};

// Initialize admin dashboard when DOM is loaded
document.addEventListener("DOMContentLoaded", () => {
  admin.init();
});

// Global functions for HTML onclick handlers
function openModal(modalId) {
  document.getElementById(modalId).classList.add("active");
}

function closeModal(modalId) {
  document.getElementById(modalId).classList.remove("active");
}

// Toast notification system
function showToast(message, type = "info") {
  const toast = document.createElement("div");
  toast.className = `toast toast-${type}`;
  toast.innerHTML = `
        <i class="fas fa-${
          type === "success"
            ? "check-circle"
            : type === "error"
            ? "exclamation-triangle"
            : type === "warning"
            ? "exclamation-circle"
            : "info-circle"
        }"></i>
        <span>${message}</span>
    `;

  document.body.appendChild(toast);

  // Animate in
  setTimeout(() => toast.classList.add("show"), 100);

  // Remove after 4 seconds
  setTimeout(() => {
    toast.classList.remove("show");
    setTimeout(() => toast.remove(), 300);
  }, 4000);
}

public/css/admin.css.content:
/* Admin Dashboard Styles */

.admin-topbar {
  background: linear-gradient(135deg, var(--primary-700) 0%, var(--primary-900) 100%);
  color: white;
}

[data-theme="dark"] .admin-topbar {
  background: linear-gradient(135deg, #1a1a1a 0%, #0a0a0a 100%);
  border-bottom: 2px solid var(--primary-600);
}

.admin-stats {
  display: flex;
  gap: var(--spacing-6);
}

.admin-stats .stat-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: var(--spacing-1);
}

.admin-stats .stat-label {
  font-size: var(--font-size-xs);
  opacity: 0.8;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.admin-stats .stat-value {
  font-size: var(--font-size-sm);
  font-weight: 600;
}

.admin-stats .stat-value.online {
  color: var(--success-400);
}

.switch-view-btn {
  position: absolute;
  bottom: var(--spacing-4);
  right: var(--spacing-4);
  background: var(--primary-500);
  color: white;
  border: none;
  padding: var(--spacing-2);
  border-radius: var(--border-radius-full);
  cursor: pointer;
  transition: all var(--transition-fast);
  text-decoration: none;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 36px;
  height: 36px;
}

.switch-view-btn:hover {
  background: var(--primary-600);
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

.admin-content {
  background: var(--bg-primary);
}

/* KPI Cards */
.kpi-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: var(--spacing-6);
  margin-bottom: var(--spacing-8);
}

.kpi-card {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
  display: flex;
  align-items: center;
  gap: var(--spacing-4);
  transition: all var(--transition-fast);
  position: relative;
  overflow: hidden;
}

.kpi-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 4px;
  height: 100%;
  background: linear-gradient(180deg, var(--primary-500) 0%, var(--primary-700) 100%);
}

.kpi-card:hover {
  transform: translateY(-2px);
  box-shadow: var(--shadow-lg);
}

.kpi-icon {
  width: 60px;
  height: 60px;
  border-radius: var(--border-radius-xl);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: var(--font-size-2xl);
  color: white;
  flex-shrink: 0;
}

.kpi-card:nth-child(1) .kpi-icon {
  background: linear-gradient(135deg, var(--primary-500) 0%, var(--primary-700) 100%);
}

.kpi-card:nth-child(2) .kpi-icon {
  background: linear-gradient(135deg, var(--success-500) 0%, var(--success-700) 100%);
}

.kpi-card:nth-child(3) .kpi-icon {
  background: linear-gradient(135deg, var(--warning-500) 0%, var(--warning-700) 100%);
}

.kpi-card:nth-child(4) .kpi-icon {
  background: linear-gradient(135deg, var(--error-500) 0%, var(--error-700) 100%);
}

.kpi-content {
  flex: 1;
}

.kpi-value {
  font-size: var(--font-size-3xl);
  font-weight: 700;
  color: var(--text-primary);
  margin-bottom: var(--spacing-1);
  font-feature-settings: 'tnum';
}

.kpi-label {
  font-size: var(--font-size-sm);
  color: var(--text-secondary);
  margin-bottom: var(--spacing-2);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.kpi-change {
  font-size: var(--font-size-xs);
  font-weight: 600;
  padding: var(--spacing-1) var(--spacing-2);
  border-radius: var(--border-radius-sm);
}

.kpi-change.positive {
  background-color: var(--success-100);
  color: var(--success-700);
}

[data-theme="dark"] .kpi-change.positive {
  background-color: rgba(76, 175, 80, 0.2);
  color: var(--success-400);
}

.kpi-change.negative {
  background-color: var(--error-100);
  color: var(--error-700);
}

[data-theme="dark"] .kpi-change.negative {
  background-color: rgba(244, 67, 54, 0.2);
  color: var(--error-400);
}

.kpi-change.neutral {
  background-color: var(--neutral-100);
  color: var(--neutral-700);
}

[data-theme="dark"] .kpi-change.neutral {
  background-color: rgba(158, 158, 158, 0.2);
  color: var(--neutral-400);
}

/* Admin Charts */
.admin-charts {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: var(--spacing-6);
  margin-bottom: var(--spacing-8);
}

.chart-card {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
}

.chart-card .chart-area {
  height: 250px;
}

/* Admin Activity */
.admin-activity {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
}

.activity-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-6);
}

.activity-actions {
  display: flex;
  gap: var(--spacing-2);
}

.activity-header h3 {
  font-size: var(--font-size-lg);
  font-weight: 600;
  color: var(--text-primary);
  margin: 0;
}

.activity-list {
  max-height: 400px;
  overflow-y: auto;
}

.activity-item {
  display: flex;
  align-items: center;
  gap: var(--spacing-4);
  padding: var(--spacing-4);
  border-radius: var(--border-radius-lg);
  margin-bottom: var(--spacing-2);
  transition: background-color var(--transition-fast);
}

.activity-item:hover {
  background-color: var(--bg-hover);
}

.activity-item:last-child {
  margin-bottom: 0;
}

.activity-icon {
  width: 36px;
  height: 36px;
  border-radius: var(--border-radius-full);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: var(--font-size-sm);
  color: white;
  flex-shrink: 0;
}

.activity-icon.user {
  background-color: var(--primary-500);
}

.activity-icon.withdrawal {
  background-color: var(--warning-500);
}

.activity-icon.trade {
  background-color: var(--success-500);
}

.activity-icon.system {
  background-color: var(--neutral-500);
}

.activity-details {
  flex: 1;
}

.activity-title {
  font-weight: 600;
  color: var(--text-primary);
  margin-bottom: var(--spacing-1);
  font-size: var(--font-size-sm);
}

.activity-subtitle {
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
}

.activity-time {
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
  text-align: right;
  white-space: nowrap;
}

/* Page Header */
.page-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-8);
  gap: var(--spacing-6);
}

.header-actions {
  display: flex;
  align-items: center;
  gap: var(--spacing-4);
}

.header-actions input {
  min-width: 250px;
}

.header-filters {
  display: flex;
  gap: var(--spacing-1);
}

.filter-btn {
  background: none;
  border: 1px solid var(--border-primary);
  padding: var(--spacing-2) var(--spacing-4);
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-sm);
  font-weight: 500;
  color: var(--text-secondary);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.filter-btn:hover,
.filter-btn.active {
  background-color: var(--primary-500);
  border-color: var(--primary-500);
  color: white;
}

/* Admin Tables */
.admin-table {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  overflow: hidden;
  margin-bottom: var(--spacing-6);
}

.admin-table table {
  width: 100%;
  border-collapse: collapse;
}

.admin-table thead {
  background: var(--bg-tertiary);
}

.admin-table th {
  padding: var(--spacing-4);
  text-align: left;
  font-size: var(--font-size-xs);
  font-weight: 600;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  border-bottom: 1px solid var(--border-secondary);
  white-space: nowrap;
}

.admin-table td {
  padding: var(--spacing-4);
  border-bottom: 1px solid var(--border-secondary);
  font-size: var(--font-size-sm);
  vertical-align: middle;
}

.admin-table tbody tr:hover {
  background-color: var(--bg-hover);
}

.admin-table tbody tr:last-child td {
  border-bottom: none;
}

/* User Column */
.user-cell {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
}

.user-cell img {
  width: 32px;
  height: 32px;
  border-radius: var(--border-radius-full);
  object-fit: cover;
  border: 2px solid var(--border-secondary);
}

.user-details .user-name {
  font-weight: 600;
  color: var(--text-primary);
  display: block;
  margin-bottom: 2px;
}

.user-details .user-email {
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
}

/* Role Badge */
.role-badge {
  padding: var(--spacing-1) var(--spacing-2);
  border-radius: var(--border-radius-sm);
  font-size: var(--font-size-xs);
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.role-badge.admin {
  background-color: var(--error-100);
  color: var(--error-700);
}

[data-theme="dark"] .role-badge.admin {
  background-color: rgba(244, 67, 54, 0.2);
  color: var(--error-400);
}

.role-badge.user {
  background-color: var(--primary-100);
  color: var(--primary-700);
}

[data-theme="dark"] .role-badge.user {
  background-color: rgba(33, 150, 243, 0.2);
  color: var(--primary-400);
}

/* Verification Badge */
.verification-badge {
  padding: var(--spacing-1) var(--spacing-2);
  border-radius: var(--border-radius-sm);
  font-size: var(--font-size-xs);
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
}

.verification-badge.verified {
  background-color: var(--success-100);
  color: var(--success-700);
}

[data-theme="dark"] .verification-badge.verified {
  background-color: rgba(76, 175, 80, 0.2);
  color: var(--success-400);
}

.verification-badge.unverified {
  background-color: var(--error-100);
  color: var(--error-700);
}

[data-theme="dark"] .verification-badge.unverified {
  background-color: rgba(244, 67, 54, 0.2);
  color: var(--error-400);
}

/* Balance Display */
.balance-display {
  font-weight: 700;
  font-feature-settings: 'tnum';
  color: var(--text-primary);
}

/* Admin Forms */
.admin-form .form-group {
  margin-bottom: var(--spacing-4);
}

.admin-form label {
  display: block;
  margin-bottom: var(--spacing-2);
  font-size: var(--font-size-sm);
  font-weight: 500;
  color: var(--text-secondary);
}

.admin-form input,
.admin-form select,
.admin-form textarea {
  width: 100%;
  padding: var(--spacing-3) var(--spacing-4);
  border: 1px solid var(--border-primary);
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-sm);
  background-color: var(--surface);
  color: var(--text-primary);
  transition: all var(--transition-fast);
}

.admin-form input:focus,
.admin-form select:focus,
.admin-form textarea:focus {
  outline: none;
  border-color: var(--border-focus);
  box-shadow: 0 0 0 3px rgba(33, 150, 243, 0.1);
}

/* Balance Adjustments */
.balance-adjustments {
  background: var(--bg-tertiary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
  margin: var(--spacing-4) 0;
}

.balance-adjustments h4 {
  font-size: var(--font-size-base);
  font-weight: 600;
  color: var(--text-primary);
  margin: 0 0 var(--spacing-4) 0;
}

.balance-row {
  display: grid;
  grid-template-columns: 1fr 2fr auto;
  gap: var(--spacing-3);
  align-items: end;
  margin-bottom: var(--spacing-3);
}

.balance-row:last-child {
  margin-bottom: 0;
}

.balance-row label {
  font-size: var(--font-size-sm);
  font-weight: 500;
  color: var(--text-secondary);
  align-self: center;
}

/* Withdrawal Details */
.withdrawal-details {
  background: var(--bg-tertiary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
  margin-bottom: var(--spacing-6);
}

.withdrawal-details h4 {
  font-size: var(--font-size-base);
  font-weight: 600;
  color: var(--text-primary);
  margin: 0 0 var(--spacing-4) 0;
}

.detail-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-2);
  font-size: var(--font-size-sm);
}

.detail-row:last-child {
  margin-bottom: 0;
}

.detail-label {
  color: var(--text-tertiary);
}

.detail-value {
  font-weight: 600;
  color: var(--text-primary);
}

.admin-actions textarea {
  margin-bottom: var(--spacing-4);
  resize: vertical;
}

.action-buttons {
  display: flex;
  gap: var(--spacing-3);
  justify-content: flex-end;
}

/* Broadcast Section */
.broadcast-section {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
  margin-bottom: var(--spacing-8);
}

.broadcast-header {
  margin-bottom: var(--spacing-6);
}

.broadcast-header h3 {
  font-size: var(--font-size-lg);
  font-weight: 600;
  color: var(--text-primary);
  margin: 0;
}

.broadcast-form .form-group {
  margin-bottom: var(--spacing-4);
}

.user-selector {
  max-height: 200px;
  overflow-y: auto;
  border: 1px solid var(--border-primary);
  border-radius: var(--border-radius-md);
  padding: var(--spacing-3);
}

.user-selector label {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-2);
  border-radius: var(--border-radius-sm);
  cursor: pointer;
  transition: background-color var(--transition-fast);
}

.user-selector label:hover {
  background-color: var(--bg-hover);
}

.user-selector input[type="checkbox"] {
  width: auto;
}

/* Sent Notifications */
.sent-notifications {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
}

.sent-header {
  margin-bottom: var(--spacing-6);
}

.sent-header h3 {
  font-size: var(--font-size-lg);
  font-weight: 600;
  color: var(--text-primary);
  margin: 0;
}

.sent-list {
  max-height: 400px;
  overflow-y: auto;
}

.sent-item {
  background: var(--bg-tertiary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
  margin-bottom: var(--spacing-3);
}

.sent-item:last-child {
  margin-bottom: 0;
}

.sent-header-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-2);
}

.sent-title {
  font-weight: 600;
  color: var(--text-primary);
  font-size: var(--font-size-sm);
  margin: 0;
}

.sent-time {
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
}

.sent-recipients {
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
  margin-bottom: var(--spacing-2);
}

.sent-message {
  font-size: var(--font-size-sm);
  color: var(--text-secondary);
  line-height: var(--line-height-normal);
  margin: 0;
}

/* Mobile Responsive */
@media (max-width: 1200px) {
  .kpi-grid {
    grid-template-columns: repeat(2, 1fr);
  }

  .admin-charts {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 768px) {
  .admin-stats {
    display: none;
  }

  .kpi-grid {
    grid-template-columns: 1fr;
    gap: var(--spacing-4);
  }

  .kpi-card {
    padding: var(--spacing-4);
  }

  .kpi-value {
    font-size: var(--font-size-2xl);
  }

  .page-header {
    flex-direction: column;
    align-items: stretch;
    gap: var(--spacing-4);
  }

  .header-actions {
    flex-direction: column;
    align-items: stretch;
  }

  .header-actions input {
    min-width: auto;
  }

  .header-filters {
    justify-content: center;
    flex-wrap: wrap;
  }

  .admin-table {
    overflow-x: auto;
  }

  .admin-table th,
  .admin-table td {
    padding: var(--spacing-2);
    font-size: var(--font-size-xs);
  }

  .user-cell {
    flex-direction: column;
    text-align: center;
    gap: var(--spacing-2);
  }

  .user-cell img {
    width: 28px;
    height: 28px;
  }

  .balance-row {
    grid-template-columns: 1fr;
    gap: var(--spacing-2);
  }

  .action-buttons {
    flex-direction: column;
  }

  .broadcast-form .form-group button {
    width: 100%;
  }
}

@media (max-width: 480px) {
  .kpi-card {
    flex-direction: column;
    text-align: center;
  }

  .kpi-icon {
    width: 50px;
    height: 50px;
    font-size: var(--font-size-xl);
  }

  .kpi-value {
    font-size: var(--font-size-xl);
  }

  .admin-table th,
  .admin-table td {
    padding: var(--spacing-1);
  }

  .user-details .user-name {
    font-size: var(--font-size-xs);
  }

  .user-details .user-email {
    font-size: 10px;
  }

  .filter-btn {
    flex: 1;
    text-align: center;
  }

  .header-filters {
    flex-direction: column;
    gap: var(--spacing-2);
  }

  .activity-actions {
    flex-direction: column;
    gap: var(--spacing-2);
  }

  .activity-actions .btn {
    font-size: var(--font-size-xs);
    padding: var(--spacing-1) var(--spacing-2);
  }
}

