// Firebase compat adapter - fixed version with proper initialization
let firebaseClient = null;
let _resolveFirebaseReady;
export const firebaseReady = new Promise((res) => {
  _resolveFirebaseReady = res;
});

const MAX_WAIT_TIME = 10000; // 10 seconds max wait

async function waitForFirebaseInit() {
  const startTime = Date.now();

  while (Date.now() - startTime < MAX_WAIT_TIME) {
    // Check if firebase-config.js has initialized everything
    if (
      window.FIREBASE_INITIALIZED &&
      window.FIREBASE_AUTH &&
      window.FIREBASE_DB &&
      window.firebase
    ) {
      return {
        firebase: window.firebase,
        auth: window.FIREBASE_AUTH,
        db: window.FIREBASE_DB,
      };
    }

    // Wait 100ms before checking again
    await new Promise((r) => setTimeout(r, 100));
  }

  throw new Error("Firebase initialization timeout");
}

async function init() {
  try {
    console.log("🔄 Firebase adapter waiting for initialization...");
    const ctx = await waitForFirebaseInit();

    if (!ctx || !ctx.auth || !ctx.db) {
      throw new Error("Firebase context incomplete");
    }

    firebaseClient = {
      firebase: ctx.firebase,
      auth: ctx.auth,
      db: ctx.db,
    };

    console.log("✅ Firebase adapter ready");
    _resolveFirebaseReady(firebaseClient);
  } catch (err) {
    console.error("❌ Firebase adapter init error:", err);
    _resolveFirebaseReady(null);
  }
}

// Start initialization
init();

// Helper to fetch user profile from Firestore
async function fetchProfile(uid) {
  if (!firebaseClient || !firebaseClient.db) {
    console.error("Firestore not available");
    return null;
  }

  try {
    const doc = await firebaseClient.db.collection("profiles").doc(uid).get();
    if (!doc.exists) {
      console.log("No profile found for user:", uid);
      return null;
    }
    return { id: doc.id, ...doc.data() };
  } catch (e) {
    console.error("fetchProfile error:", e);
    return null;
  }
}

// Sign up new user
export async function signUp(email, password, fullName) {
  if (!firebaseClient) {
    return { data: null, error: new Error("Firebase not initialized") };
  }

  try {
    console.log("📝 Creating user account...");
    const userCred = await firebaseClient.auth.createUserWithEmailAndPassword(
      email,
      password
    );
    const user = userCred.user;

    console.log("✅ User created:", user.uid);

    // Create profile in Firestore
    const profile = {
      id: user.uid,
      email,
      full_name: fullName,
      role: "user",
      status: "active",
      balances: { USD: 0, BTC: 0, ETH: 0 },
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
    };

    if (firebaseClient.db) {
      console.log("📝 Creating user profile...");
      await firebaseClient.db.collection("profiles").doc(user.uid).set(profile);
      console.log("✅ Profile created");
    } else {
      console.warn("⚠️ Firestore not available: profile write skipped");
    }

    // Send verification email (if enabled)
    const requireVerification = window.REQUIRE_EMAIL_VERIFICATION !== false;
    if (requireVerification) {
      try {
        console.log("📧 Sending verification email...");
        await user.sendEmailVerification();
        console.log("✅ Verification email sent");
      } catch (err) {
        console.error("❌ Verification email failed:", err);
        return {
          data: { user, profile },
          error: new Error(
            "Account created but verification email failed: " +
              (err?.message || String(err))
          ),
        };
      }
    } else {
      console.log("⚠️ Email verification disabled");
    }

    return { data: { user, profile }, error: null };
  } catch (err) {
    console.error("❌ Signup error:", err);
    return {
      data: null,
      error: new Error(err?.message || "Signup failed"),
    };
  }
}

// Sign in existing user
export async function signIn(email, password) {
  if (!firebaseClient) {
    return { data: null, error: new Error("Firebase not initialized") };
  }

  try {
    console.log("🔐 Signing in user...");
    const cred = await firebaseClient.auth.signInWithEmailAndPassword(
      email,
      password
    );
    const user = cred.user;

    console.log("✅ User signed in:", user.uid);
    console.log("Email verified:", user.emailVerified);

    // Fetch profile
    const profile = await fetchProfile(user.uid);

    return { data: { user, profile }, error: null };
  } catch (err) {
    console.error("❌ Sign in error:", err);
    return {
      data: null,
      error: new Error(err?.message || "Sign in failed"),
    };
  }
}

// Sign out user
export async function signOut() {
  if (!firebaseClient) {
    return { error: new Error("Firebase not initialized") };
  }

  try {
    console.log("🚪 Signing out...");
    await firebaseClient.auth.signOut();
    console.log("✅ Signed out");
    return { error: null };
  } catch (err) {
    console.error("❌ Sign out error:", err);
    return { error: err };
  }
}

// Reset password
export async function resetPassword(email) {
  if (!firebaseClient) {
    return { error: new Error("Firebase not initialized") };
  }

  try {
    console.log("📧 Sending password reset email...");
    await firebaseClient.auth.sendPasswordResetEmail(email);
    console.log("✅ Password reset email sent");
    return { error: null };
  } catch (err) {
    console.error("❌ Password reset error:", err);
    return { error: err };
  }
}

// Resend confirmation email
export async function resendConfirmation() {
  if (!firebaseClient) {
    return { error: new Error("Firebase not initialized") };
  }

  const user = firebaseClient.auth.currentUser;
  if (!user) {
    return { error: new Error("No signed-in user") };
  }

  try {
    console.log("📧 Resending verification email...");
    await user.sendEmailVerification();
    console.log("✅ Verification email sent");
    return { error: null };
  } catch (err) {
    console.error("❌ Resend verification error:", err);
    return { error: err };
  }
}

// Send verification for current user
export async function sendVerificationForCurrentUser() {
  return resendConfirmation();
}

// Get current user
// Find the getCurrentUser function in firebase.js and replace it with this:

export async function getCurrentUser() {
  try {
    const fb = await firebaseReady;
    if (!fb || !fb.auth) {
      console.log("   Firebase not ready in getCurrentUser");
      return { user: null, profile: null };
    }

    // Get current user from Firebase Auth
    const user = fb.auth.currentUser;
    console.log("   getCurrentUser: Firebase user:", user ? user.uid : "null");

    if (!user) {
      return { user: null, profile: null };
    }

    // Try to get user profile from Firestore
    let profile = null;
    try {
      const profileDoc = await fb.db.collection("profiles").doc(user.uid).get();
      if (profileDoc.exists) {
        profile = { id: profileDoc.id, ...profileDoc.data() };
        console.log("   getCurrentUser: Profile loaded:", profile.full_name);
      } else {
        console.log("   getCurrentUser: No profile found for user");
      }
    } catch (profileError) {
      console.warn("   Failed to load profile:", profileError);
    }

    return { user, profile };
  } catch (err) {
    console.error("❌ getCurrentUser error:", err);
    return { user: null, profile: null };
  }
}

// Update user profile
export async function updateUserProfile(userId, updates) {
  if (!firebaseClient || !firebaseClient.db) {
    return { data: null, error: new Error("Firebase not initialized") };
  }

  try {
    await firebaseClient.db.collection("profiles").doc(userId).update(updates);
    const doc = await firebaseClient.db
      .collection("profiles")
      .doc(userId)
      .get();
    return { data: { id: doc.id, ...doc.data() }, error: null };
  } catch (err) {
    console.error("❌ Update profile error:", err);
    return { data: null, error: err };
  }
}

// Get Firebase client
export function getFirebaseClient() {
  return firebaseClient;
}

public/css/dashboard.css.content:
/* Dashboard CSS - Dark Mode default */
:root {
    --primary-color: #1a73e8;
    --primary-dark: #1557b0;
    --success-color: #34a853;
    --danger-color: #ea4335;
    --warning-color: #fbbc04;

    /* Dark surfaces */
    --surface-color: #0f1720;
    --surface-variant: #12161b;
    --background-color: #0b0f14;

    --text-primary: #e6eef8;
    --text-secondary: #b3c0cc;
    --text-muted: #8b95a0;

    --border-color: rgba(255,255,255,0.06);
    --shadow-light: 0 1px 2px rgba(0,0,0,0.6);
    --shadow-medium: 0 2px 8px rgba(0,0,0,0.7);
    --shadow-heavy: 0 6px 20px rgba(0,0,0,0.75);
    --card-glow: 0 8px 30px rgba(26,115,232,0.06);

    --border-radius: 8px;
    --border-radius-lg: 12px;
    --transition: all 0.18s ease;
    --header-height: 64px;
    --bottom-nav-height: 72px;
}

/* Global Reset */
* { margin: 0; padding: 0; box-sizing: border-box; }
html, body { height: 100%; }
body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background-color: var(--background-color);
    color: var(--text-primary);
    line-height: 1.5;
    overflow-x: hidden;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

/* App Container */
.app-container {
    width: 100%;
    max-width: 428px;
    margin: 0 auto;
    background: var(--surface-variant);
    min-height: 100vh;
    position: relative;
}

/* Header */
.app-header {
    height: var(--header-height);
    background: transparent;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 16px;
    border-bottom: 1px solid var(--border-color);
    position: sticky;
    top: 0;
    z-index: 100;
}
.header-left { display:flex; align-items:center; gap:12px; }
.user-avatar { width:40px; height:40px; border-radius:50%; overflow:hidden; border:2px solid rgba(255,255,255,0.04); }
.user-avatar img { width:100%; height:100%; object-fit:cover; }
.user-info { display:flex; flex-direction:column; }
.greeting { font-size:12px; color: var(--text-secondary); font-weight:400; }
.username { font-size:16px; font-weight:600; color: var(--text-primary); }
.header-right { display:flex; align-items:center; gap:8px; }
.header-btn {
    width:40px; height:40px; border:none; background:transparent; border-radius:50%;
    display:flex; align-items:center; justify-content:center; color:var(--text-secondary);
    cursor:pointer; transition: var(--transition); position:relative;
}
.header-btn:hover { background: rgba(255,255,255,0.02); color: var(--text-primary); }
.notification-badge {
    position:absolute; top:8px; right:8px; background: var(--danger-color); color: white;
    font-size:10px; font-weight:600; padding:2px 6px; border-radius:10px; min-width:16px; text-align:center;
}

/* Main Content */
.main-content {
    min-height: calc(100vh - var(--header-height) - var(--bottom-nav-height));
    padding-bottom: 16px;
}

/* Page wrapper */
.page {
    display: none;
    padding: 16px;
    width: 100%;
    height: calc(100vh - var(--header-height));
    overflow: auto;
    animation: fadeIn 0.25s ease;
}
.page.active { display:block; }

/* Portfolio Card */
.portfolio-card {
    background: linear-gradient(135deg, var(--primary-color), var(--primary-dark));
    color: white;
    border-radius: var(--border-radius-lg);
    padding: 24px;
    margin-bottom: 24px;
    box-shadow: var(--card-glow), var(--shadow-heavy);
}
.portfolio-header { display:flex; justify-content:space-between; align-items:center; margin-bottom:16px; }
.portfolio-header h2 { font-size:16px; font-weight:500; opacity:0.95; }
.visibility-toggle {
    background: rgba(255,255,255,0.08); border:none; color:white; width:32px; height:32px; border-radius:50%;
    display:flex; align-items:center; justify-content:center; cursor:pointer;
}
.balance-amount { font-size:36px; font-weight:700; margin-bottom:8px; letter-spacing:-1px; }
.balance-change { display:flex; align-items:center; gap:4px; font-size:14px; font-weight:500; margin-bottom:24px; }
.balance-change.positive { color: var(--success-color); }
.balance-change.negative { color: var(--warning-color); }

/* Quick Stats */
.quick-stats { display:grid; grid-template-columns: repeat(3,1fr); gap:16px; }
.stat-item { text-align:center; opacity:0.95; }
.stat-label { display:block; font-size:12px; margin-bottom:4px; opacity:0.85; color:var(--text-secondary); }
.stat-value { display:block; font-size:16px; font-weight:600; color:var(--text-primary); }
.stat-value.positive { color: var(--success-color); }
.stat-value.negative { color: var(--danger-color); }

/* Quick Actions */
.quick-actions { display:grid; grid-template-columns: repeat(4,1fr); gap:12px; margin-bottom:24px; }
.action-btn {
    background: var(--surface-color); border: 1px solid var(--border-color); border-radius: var(--border-radius);
    padding:16px 8px; display:flex; flex-direction:column; align-items:center; gap:8px; cursor:pointer;
    transition: var(--transition); text-decoration:none; color: var(--text-primary);
}
.action-btn:hover { border-color: var(--primary-color); box-shadow: var(--shadow-light); }
.action-btn.primary { background: linear-gradient(180deg,var(--primary-color),var(--primary-dark)); color: #fff; border-color: transparent; }
.action-btn.primary:hover { transform: translateY(-2px); }
.action-btn i { font-size:18px; }
.action-btn span { font-size:12px; font-weight:500; }

/* Section card */
.section-card { background: var(--surface-color); border-radius: var(--border-radius); padding:16px; margin-bottom:16px; box-shadow: var(--shadow-light); border: 1px solid var(--border-color); }
.section-header { display:flex; justify-content:space-between; align-items:center; margin-bottom:16px; }
.section-header h3 { font-size:18px; font-weight:600; }
.section-action { background:none; border:none; color:var(--primary-color); font-size:14px; font-weight:500; cursor:pointer; padding:4px 8px; border-radius:4px; transition: var(--transition); }
.section-action:hover { background: rgba(26,115,232,0.06); }

/* Watchlist */
.watchlist-item { display:flex; justify-content:space-between; align-items:center; padding:12px 0; border-bottom: 1px solid var(--border-color); }
.watchlist-item:last-child { border-bottom:none; }
.watchlist-symbol { display:flex; flex-direction:column; }
.symbol-name { font-weight:600; font-size:16px; }
.symbol-description { font-size:12px; color: var(--text-secondary); }
.watchlist-price { display:flex; flex-direction:column; align-items:flex-end; }
.price { font-weight:600; font-size:16px; color:var(--text-primary); }
.price-change { font-size:12px; padding:2px 6px; border-radius:4px; font-weight:500; }
.price-change.positive { color: var(--success-color); background: rgba(52,168,83,0.06); }
.price-change.negative { color: var(--danger-color); background: rgba(234,67,53,0.06); }

/* Trading Page */
.trading-header { background: transparent; padding:16px; border-bottom: 1px solid var(--border-color); margin: -16px -16px 16px -16px; }
.symbol-selector { display:flex; align-items:center; gap:8px; cursor:pointer; margin-bottom:8px; padding:8px; border-radius:var(--border-radius); transition: var(--transition); }
.symbol-selector:hover { background: rgba(255,255,255,0.02); }
.current-symbol { font-size:20px; font-weight:700; color: var(--text-primary); }
.symbol-info { display:flex; justify-content:space-between; align-items:center; }
.price-display { display:flex; gap:16px; }
.bid-price, .ask-price { font-size:18px; font-weight:600; font-family: 'Roboto Mono', monospace; }
.bid-price { color: var(--danger-color); }
.ask-price { color: var(--success-color); }

/* Chart containers */
.trading-chart-section {
    background: var(--surface-color); border-radius: var(--border-radius); margin-bottom:16px; overflow:hidden;
    box-shadow: var(--shadow-light); border: 1px solid var(--border-color);
    min-height: 300px; height: 400px; position: relative;
}
.tradingview-widget-container {
    width: 100%; height: 100%; position: relative; overflow: hidden;
}
.tradingview-widget-container iframe {
    width: 100% !important;
    height: 100% !important;
    border: none !important;
    display: block !important;
}

/* Enhanced Trade Panel */
.trade-panel {
    background: var(--surface-color); border-radius: var(--border-radius); margin-bottom:16px;
    box-shadow: var(--shadow-light); border: 1px solid var(--border-color); padding: 16px;
    display: flex; flex-direction: column; gap: 16px;
}

/* Trade Section */
.trade-section {
    display: flex; flex-direction: column; gap: 8px;
}

.trade-section-title {
    font-size: 14px; font-weight: 600; color: var(--text-secondary); text-transform: uppercase;
    letter-spacing: 0.5px; margin-bottom: 4px;
}

/* Order Type Buttons */
.order-type-buttons {
    display: flex; gap: 8px;
}

.order-type-btn {
    flex: 1; padding: 10px 16px; border-radius: var(--border-radius); border: 1px solid var(--border-color);
    background: transparent; color: var(--text-secondary); font-size: 14px; font-weight: 500;
    cursor: pointer; transition: var(--transition);
}

.order-type-btn:hover {
    background: rgba(255,255,255,0.02); border-color: var(--primary-color);
}

.order-type-btn.active {
    background: var(--primary-color); color: #fff; border-color: var(--primary-color);
}

/* Trade Direction Buttons */
.trade-direction-buttons {
    display: flex; gap: 8px;
}

.direction-btn {
    flex: 1; padding: 12px; border-radius: var(--border-radius); border: 2px solid transparent;
    background: transparent; color: var(--text-primary); font-size: 14px; font-weight: 600;
    cursor: pointer; transition: var(--transition); display: flex; align-items: center;
    justify-content: center; gap: 8px;
}

.direction-btn.buy {
    border-color: var(--success-color); color: var(--success-color);
}

.direction-btn.buy:hover {
    background: rgba(52,168,83,0.08);
}

.direction-btn.buy.active {
    background: var(--success-color); color: #fff;
}

.direction-btn.sell {
    border-color: var(--danger-color); color: var(--danger-color);
}

.direction-btn.sell:hover {
    background: rgba(234,67,53,0.08);
}

.direction-btn.sell.active {
    background: var(--danger-color); color: #fff;
}

/* Volume Input */
.volume-input-container {
    display: flex; flex-direction: column; gap: 8px;
}

.volume-input {
    padding: 12px; border-radius: var(--border-radius); border: 1px solid var(--border-color);
    background: transparent; color: var(--text-primary); font-size: 16px;
    font-family: 'Roboto Mono', monospace;
}

.volume-input:focus {
    outline: none; border-color: var(--primary-color); box-shadow: 0 0 0 4px rgba(26,115,232,0.06);
}

.volume-presets {
    display: flex; gap: 6px;
}

.volume-preset {
    flex: 1; padding: 8px 12px; border-radius: 6px; border: 1px solid var(--border-color);
    background: transparent; color: var(--text-secondary); font-size: 12px; font-weight: 500;
    cursor: pointer; transition: var(--transition);
}

.volume-preset:hover {
    background: rgba(255,255,255,0.02); border-color: var(--primary-color);
}

/* Price Section */
.price-section {
    animation: slideDown 0.3s ease;
}

@keyframes slideDown {
    from { opacity: 0; transform: translateY(-10px); }
    to { opacity: 1; transform: translateY(0); }
}

.price-input {
    padding: 12px; border-radius: var(--border-radius); border: 1px solid var(--border-color);
    background: transparent; color: var(--text-primary); font-size: 16px;
    font-family: 'Roboto Mono', monospace; width: 100%;
}

.price-input:focus {
    outline: none; border-color: var(--primary-color); box-shadow: 0 0 0 4px rgba(26,115,232,0.06);
}

/* Risk Management */
.risk-inputs {
    display: grid; grid-template-columns: 1fr 1fr; gap: 12px;
}

.risk-input-group {
    display: flex; flex-direction: column; gap: 4px;
}

.risk-label {
    font-size: 12px; color: var(--text-secondary); font-weight: 500;
}

.risk-input {
    padding: 10px; border-radius: 6px; border: 1px solid var(--border-color);
    background: transparent; color: var(--text-primary); font-size: 14px;
    font-family: 'Roboto Mono', monospace;
}

.risk-input:focus {
    outline: none; border-color: var(--primary-color); box-shadow: 0 0 0 3px rgba(26,115,232,0.06);
}

/* Order Summary */
.order-summary {
    background: var(--surface-variant); border-radius: var(--border-radius); padding: 12px;
    border: 1px solid var(--border-color);
}

.summary-row {
    display: flex; justify-content: space-between; align-items: center; padding: 6px 0;
    font-size: 14px;
}

.summary-row.total {
    border-top: 1px solid var(--border-color); margin-top: 8px; padding-top: 12px;
    font-weight: 600; font-size: 16px;
}

.summary-label {
    color: var(--text-secondary);
}

.summary-value {
    color: var(--text-primary); font-weight: 500; font-family: 'Roboto Mono', monospace;
}

/* Execute Trade Button */
.execute-trade-btn {
    padding: 16px; border-radius: var(--border-radius); border: none; font-weight: 700;
    background: var(--primary-color); color: #fff; cursor: pointer; font-size: 16px;
    display: flex; align-items: center; justify-content: center; gap: 8px;
    transition: var(--transition);
}

.execute-trade-btn:hover {
    background: var(--primary-dark); transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(26,115,232,0.3);
}

.execute-trade-btn:active {
    transform: translateY(0);
}

/* Legacy trade input styles for backward compatibility */
.trade-input {
    padding: 12px; border-radius: 8px; border: 1px solid var(--border-color); background: transparent;
    color: var(--text-primary); font-size: 16px; font-family: 'Roboto Mono', monospace;
}

.trade-input:focus {
    outline: none; border-color: var(--primary-color); box-shadow: 0 0 0 4px rgba(26,115,232,0.06);
}

/* Legacy order buttons for backward compatibility */
.order-btn {
    padding: 12px; border-radius: var(--border-radius); cursor: pointer; font-weight: 600;
    display: flex; align-items: center; justify-content: center; gap: 8px; border: 2px solid transparent;
}

.order-btn.buy {
    background: rgba(52,168,83,0.08); color: var(--success-color);
}

.order-btn.buy.active {
    background: var(--success-color); color: #fff; border-color: var(--success-color);
}

.order-btn.sell {
    background: rgba(234,67,53,0.08); color: var(--danger-color);
}

.order-btn.sell.active {
    background: var(--danger-color); color: #fff; border-color: var(--danger-color);
}

/* Positions Section */
.positions-section { background: var(--surface-color); border-radius: var(--border-radius); padding: 16px; box-shadow: var(--shadow-light); border:1px solid var(--border-color); margin-bottom: 16px; }
.position-count { background: var(--primary-color); color: #fff; padding:4px 8px; border-radius:12px; font-size:12px; font-weight:600; }

/* Transactions List */
.transaction-item {
    display:flex; align-items:center; gap:12px; padding:16px; background: var(--surface-color); border-radius: var(--border-radius);
    margin-bottom:8px; box-shadow: var(--shadow-light); border:1px solid var(--border-color);
}
.transaction-icon { width:40px; height:40px; border-radius:50%; display:flex; align-items:center; justify-content:center; color:#fff; flex-shrink:0; }
.transaction-icon.deposit { background: var(--success-color); }
.transaction-icon.withdrawal { background: var(--danger-color); }
.transaction-icon.trade { background: var(--primary-color); }
.transaction-details { flex:1; }
.transaction-title { font-weight:600; font-size:16px; margin-bottom:2px; }
.transaction-subtitle { font-size:12px; color: var(--text-secondary); }
.transaction-amount { display:flex; flex-direction:column; align-items:flex-end; }
.amount-primary { font-weight:600; font-size:16px; }
.amount-primary.positive { color: var(--success-color); }
.amount-primary.negative { color: var(--danger-color); }
.amount-secondary { font-size:12px; color: var(--text-secondary); }

/* Notifications */
.notification-item { display:flex; align-items:flex-start; gap:12px; padding:16px; background: var(--surface-color); border-radius: var(--border-radius); margin-bottom:8px; box-shadow: var(--shadow-light); border:1px solid var(--border-color); position:relative; }
.notification-item.unread { border-left: 4px solid var(--primary-color); }
.notification-item.unread::before { content:''; position:absolute; top:16px; right:16px; width:8px; height:8px; background: var(--primary-color); border-radius:50%; }
.notification-icon { width:40px; height:40px; border-radius:50%; display:flex; align-items:center; justify-content:center; font-size:16px; color:#fff; flex-shrink:0; }
.notification-icon.info { background: var(--primary-color); }
.notification-icon.success { background: var(--success-color); }
.notification-icon.warning { background: var(--warning-color); color:#111; }
.notification-icon.danger { background: var(--danger-color); }
.notification-content { flex:1; }
.notification-title { font-weight:600; font-size:16px; margin-bottom:4px; }
.notification-message { font-size:14px; color: var(--text-secondary); line-height:1.4; margin-bottom:4px; }
.notification-time { font-size:12px; color: var(--text-muted); }

/* Profile */
.profile-header { display:flex; flex-direction:column; align-items:center; text-align:center; padding:24px 0; margin-bottom:24px; }
.profile-avatar { position:relative; margin-bottom:16px; }
.profile-avatar img { width:80px; height:80px; border-radius:50%; object-fit:cover; border:4px solid var(--primary-color); }
.profile-info h2 { font-size:24px; font-weight:700; margin-bottom:4px; }
.profile-email { color: var(--text-secondary); margin-bottom:8px; }
.verification-badge { display:inline-flex; align-items:center; gap:4px; padding:4px 12px; border-radius:12px; font-size:12px; font-weight:500; }
.verification-badge.verified { background: rgba(52,168,83,0.08); color: var(--success-color); }

/* Menu */
.profile-menu { background: var(--surface-color); border-radius: var(--border-radius); overflow:hidden; box-shadow: var(--shadow-light); border:1px solid var(--border-color); }
.menu-section { padding:16px 0; border-bottom: 1px solid var(--border-color); }
.menu-section:last-child { border-bottom:none; }
.menu-section h3 { font-size:14px; font-weight:600; color: var(--text-secondary); text-transform:uppercase; letter-spacing:0.5px; padding:0 16px; margin-bottom:8px; }
.menu-item { display:flex; align-items:center; gap:12px; padding:12px 16px; cursor:pointer; transition: var(--transition); color: var(--text-primary); text-decoration:none; }
.menu-item:hover { background: rgba(255,255,255,0.02); }
.menu-item.danger { color: var(--danger-color); }
.menu-item i:first-child { width:20px; font-size:16px; }
.menu-item span { flex:1; font-size:16px; font-weight:500; }
