@keyframes pulse { 0% { box-shadow: 0 0 0 0 rgba(52,168,83,0.7); } 70% { box-shadow: 0 0 0 10px rgba(52,168,83,0); } 100% { box-shadow: 0 0 0 0 rgba(52,168,83,0); } }

