}

.avatar-edit-btn:hover {
    background: var(--primary-dark);
    transform: scale(1.1);
}

.profile-avatar-section h4 {
    margin: 0 0 4px 0;
    color: var(--text-primary);
    font-size: 20px;
    font-weight: 600;
}

.profile-avatar-section p {
