}

.profile-stats-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 16px;
    margin-bottom: 24px;
    padding: 20px;
    background: var(--surface-variant);
    border-radius: var(--border-radius);
}

.profile-stat {
    text-align: center;
}

.profile-stat .stat-value {
