// Enhanced 3D Admin Dashboard with Full Synchronization
const admin = {
  currentUser: null,
  currentPage: "dashboard",
  users: [],
  filteredUsers: [],
  currentPageNum: 1,
  itemsPerPage: 10,
  isLoading: false,
  realTimeListeners: {},
  syncStatus: {
    users: false,
    transactions: false,
    withdrawals: false,
    trades: false,
    notifications: false,
  },
  animations: {
    fadeIn: "fadeIn 0.6s ease-out forwards",
    slideUp: "slideUp 0.8s ease-out forwards",
    scaleIn: "scaleIn 0.5s ease-out forwards",
    bounceIn: "bounceIn 0.6s cubic-bezier(0.68, -0.55, 0.265, 1.55) forwards",
  },

  // Initialize admin dashboard with enhanced 3D effects
  async init() {
    this.addLoadingAnimation();
    this.setup3DEffects();

    try {
      // Check authentication and role
      let user = null;
      // Prefer global authManager (Firebase) if available
      try {
        if (
          window.authManager &&
          typeof authManager.isAuthenticated === "function" &&
          authManager.isAuthenticated()
        ) {
          user = authManager.getUser();
          // authManager may expose profile; set validate to use that
        }
      } catch (e) {
        console.warn("authManager check failed:", e);
      }

      // Fallback to Supabase if present
      if (
        !user &&
        window.supabase &&
        supabase.auth &&
        typeof supabase.auth.getUser === "function"
      ) {
        try {
          const resp = await supabase.auth.getUser();
          user = resp?.data?.user || null;
        } catch (e) {
          user = null;
        }
      }

      if (!user) {
        this.redirectToLogin();
        return;
      }

      this.currentUser = user;
      await this.validateAdminAccess(user.id || user.uid);

      this.setupEventListeners();
      await this.loadDashboardData();
      this.showPage("dashboard");
      this.removeLoadingAnimation();

      // Add welcome animation
      this.showWelcomeAnimation();
    } catch (error) {
      console.error("Initialization error:", error);
      this.handleInitError(error);
    }
  },

  // Add loading animation
  addLoadingAnimation() {
    const loadingHTML = `
            <div id="admin-loading" style="
                position: fixed; top: 0; left: 0; width: 100%; height: 100%;
                background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                display: flex; align-items: center; justify-content: center;
                z-index: 9999; opacity: 1; transition: opacity 0.5s ease;
            ">
                <div style="text-align: center; color: white;">
                    <div style="
                        width: 80px; height: 80px; border: 4px solid rgba(255,255,255,0.3);
                        border-top: 4px solid white; border-radius: 50%;
                        animation: spin 1s linear infinite; margin: 0 auto 20px;
                    "></div>
                    <h2 style="margin: 0; font-size: 1.5rem;">Loading Admin Panel</h2>
                    <p style="margin: 10px 0 0 0; opacity: 0.8;">Initializing 3D interface...</p>
                </div>
            </div>
            <style>
                @keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }
                @keyframes fadeIn { from { opacity: 0; transform: translateY(20px); } to { opacity: 1; transform: translateY(0); } }
                @keyframes slideUp { from { opacity: 0; transform: translateY(30px); } to { opacity: 1; transform: translateY(0); } }
                @keyframes scaleIn { from { opacity: 0; transform: scale(0.9); } to { opacity: 1; transform: scale(1); } }
                @keyframes bounceIn { 0% { opacity: 0; transform: scale(0.3); } 50% { transform: scale(1.05); } 70% { transform: scale(0.9); } 100% { opacity: 1; transform: scale(1); } }
            </style>
        `;
    document.body.insertAdjacentHTML("beforeend", loadingHTML);
  },

  // Remove loading animation
  removeLoadingAnimation() {
    const loading = document.getElementById("admin-loading");
    if (loading) {
      loading.style.opacity = "0";
      setTimeout(() => loading.remove(), 500);
    }
  },

  // Setup 3D effects
  setup3DEffects() {
    // Add CSS for 3D effects
    const style = document.createElement("style");
    style.textContent = `
            .stat-card {
                transform-style: preserve-3d;
                transition: transform 0.3s ease, box-shadow 0.3s ease;
            }
            .stat-card:hover {
                transform: translateY(-5px) rotateX(5deg);
                box-shadow: 0 20px 40px rgba(0,0,0,0.2);
            }
            .menu-item {
                transform-style: preserve-3d;
                transition: transform 0.3s ease;
            }
            .menu-item:hover {
                transform: translateY(-2px) translateZ(10px);
            }
            .btn {
                transform-style: preserve-3d;
                transition: transform 0.2s ease;
            }
            .btn:hover {
                transform: translateY(-2px) translateZ(5px);
            }
            .activity-item {
                transition: transform 0.2s ease;
            }
            .activity-item:hover {
                transform: translateX(10px);
            }
        `;
    document.head.appendChild(style);
  },

  // Validate admin access
  async validateAdminAccess(userId) {
    // Try Firebase/Firestore profile first
    let profile = null;
    try {
      if (window.firebase && window.firebase.firestore) {
        const db = window.firebase.firestore();
        const docRef = db.collection("profiles").doc(userId);
        const doc = await docRef.get();
        if (doc.exists) profile = { id: doc.id, ...doc.data() };
      }
    } catch (e) {
      console.warn("Firestore profile fetch failed:", e);
    }

    // Fallback to Supabase profile if Firestore not available or missing
    if (!profile && window.supabase) {
      try {
        const { data, error } = await supabase
          .from("profiles")
          .select("role, full_name")
          .eq("id", userId)
          .single();
        if (!error) profile = data;
      } catch (e) {
        console.warn("Supabase profile fetch failed:", e);
      }
    }

    if (!profile || !["admin", "super_admin"].includes(profile.role)) {
      throw new Error("Admin access denied");
    }

    // Update header with admin name
    const header = document.querySelector(".admin-header h1");
    if (header && profile.full_name) {
      header.innerHTML = `<i class="fas fa-crown"></i> Welcome back, ${profile.full_name}`;
    }
  },

  // Redirect to login
  redirectToLogin() {
    showToast("Please log in to access the admin panel", "warning");
    setTimeout(() => {
      window.location.href = "login.html";
    }, 2000);
  },

  // Handle initialization error
  handleInitError(error) {
    this.removeLoadingAnimation();
    showToast(`Initialization failed: ${error.message}`, "error");

    // Redirect to main dashboard instead of showing access denied screen
    setTimeout(() => {
      window.location.href = "dashboard.html";
    }, 3000);
  },

  // Show welcome animation
  showWelcomeAnimation() {
    const sidebar = document.querySelector(".admin-sidebar");
    const main = document.querySelector(".admin-main");

    if (sidebar) {
      sidebar.style.animation = this.animations.slideUp;
    }
    if (main) {
      main.style.animation = this.animations.fadeIn;
    }

    // Animate stats cards
    const statCards = document.querySelectorAll(".stat-card");
    statCards.forEach((card, index) => {
      card.style.animation = `${this.animations.bounceIn} ${
        0.6 + index * 0.1
      }s`;
    });
  },

  // Setup event listeners
  setupEventListeners() {
    // Sidebar navigation
    document.querySelectorAll(".menu-item").forEach((item) => {
      item.addEventListener("click", () => {
        const page = item.dataset.page;
        this.showPage(page);
      });
    });

    // Search and filter
    const searchInput = document.getElementById("user-search");
    const filterSelect = document.getElementById("user-filter");
    if (searchInput) {
      searchInput.addEventListener("input", () => this.filterUsers());
    }
    if (filterSelect) {
      filterSelect.addEventListener("change", () => this.filterUsers());
    }

    // Broadcast form
    const broadcastForm = document.getElementById("broadcast-form");
    if (broadcastForm) {
      broadcastForm.addEventListener("submit", (e) => this.sendBroadcast(e));
    }

    // Broadcast recipients change
    const broadcastRecipients = document.getElementById("broadcast-recipients");
    const userSelector = document.getElementById("user-selector");
    if (broadcastRecipients && userSelector) {
      broadcastRecipients.addEventListener("change", () => {
        userSelector.style.display =
          broadcastRecipients.value === "select" ? "block" : "none";
        if (broadcastRecipients.value === "select") {
          this.loadUserCheckboxes();
        }
      });
    }

    // Edit user form
    const editUserForm = document.getElementById("edit-user-form");
    if (editUserForm) {
      editUserForm.addEventListener("submit", (e) => this.saveUserChanges(e));
    }
  },

  // Show page
  showPage(pageName) {
    document
      .querySelectorAll(".page")
      .forEach((page) => page.classList.remove("active"));
    document
      .querySelectorAll(".menu-item")
      .forEach((item) => item.classList.remove("active"));

    const targetPage = document.getElementById(`${pageName}-page`);
    const targetMenu = document.querySelector(
      `.menu-item[data-page="${pageName}"]`
    );

    if (targetPage) targetPage.classList.add("active");
    if (targetMenu) targetMenu.classList.add("active");

    this.currentPage = pageName;

    // Load page-specific data
    switch (pageName) {
      case "users":
        this.loadUsers();
        break;
      case "transactions":
        this.loadTransactions();
        break;
      case "withdrawals":
        this.loadWithdrawals();
        break;
      case "trades":
        this.loadTrades();
        break;
      case "logs":
        this.loadLogs();
        break;
    }
  },

  // Load dashboard data
  async loadDashboardData() {
    try {
      // Load stats
      const [usersResult, depositsResult, withdrawalsResult] =
        await Promise.all([
          supabase.from("profiles").select("id, created_at"),
          supabase.from("transactions").select("amount").eq("type", "deposit"),
          supabase.from("withdrawals").select("id").eq("status", "pending"),
        ]);

      if (usersResult.data) {
        document.getElementById("total-users").textContent =
          usersResult.data.length;
        const activeUsers = usersResult.data.filter((u) => {
          const lastLogin = new Date(u.last_login || u.created_at);
          const weekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
          return lastLogin > weekAgo;
        }).length;
        document.getElementById("active-users").textContent = activeUsers;
      }

      if (depositsResult.data) {
        const totalDeposits = depositsResult.data.reduce(
          (sum, t) => sum + (t.amount || 0),
          0
        );
        document.getElementById(
          "total-deposits"
        ).textContent = `$${totalDeposits.toFixed(2)}`;
      }

      if (withdrawalsResult.data) {
        document.getElementById("pending-withdrawals").textContent =
          withdrawalsResult.data.length;
      }

      // Load recent activity
      this.loadRecentActivity();
    } catch (error) {
      console.error("Error loading dashboard data:", error);
      showToast("Error loading dashboard data", "error");
    }
  },

  // Load recent activity
  async loadRecentActivity() {
    try {
      const { data: logs, error } = await supabase
        .from("activity_logs")
        .select("*")
        .order("created_at", { ascending: false })
        .limit(10);

      if (error) throw error;

