  // Load users
  async loadUsers() {
    try {
      const { data: users, error } = await supabase
        .from("profiles")
        .select("*")
        .order("created_at", { ascending: false });

      if (error) throw error;

      this.users = users;
      this.filterUsers();
    } catch (error) {
      console.error("Error loading users:", error);
      showToast("Error loading users", "error");
    }
  },

  // Filter users
  filterUsers() {
    const searchTerm = document
      .getElementById("user-search")
      .value.toLowerCase();
    const filterValue = document.getElementById("user-filter").value;

    this.filteredUsers = this.users.filter((user) => {
      const matchesSearch =
        !searchTerm ||
        user.full_name?.toLowerCase().includes(searchTerm) ||
        user.email?.toLowerCase().includes(searchTerm);

      let matchesFilter = true;
      switch (filterValue) {
        case "active":
          matchesFilter = user.status === "active";
          break;
        case "suspended":
          matchesFilter = user.status === "suspended";
          break;
        case "unverified":
          matchesFilter = !user.email_confirmed_at;
          break;
      }

      return matchesSearch && matchesFilter;
    });

    this.currentPageNum = 1;
    this.renderUsers();
  },

  // Render users table
  renderUsers() {
    const startIndex = (this.currentPageNum - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    const usersToShow = this.filteredUsers.slice(startIndex, endIndex);

    const tbody = document.getElementById("users-tbody");
    tbody.innerHTML = usersToShow
      .map(
        (user) => `
            <tr>
                <td>${user.full_name || "N/A"}</td>
                <td>${user.email}</td>
                <td>$${user.balance?.toFixed(2) || "0.00"}</td>
                <td><span class="status ${user.status || "active"}">${
          user.status || "active"
        }</span></td>
                <td>${this.formatDate(user.created_at)}</td>
                <td>${
                  user.last_login ? this.formatDate(user.last_login) : "Never"
                }</td>
                <td class="user-actions">
                    <button class="btn btn-small btn-primary" onclick="admin.viewUser('${
                      user.id
                    }')">View</button>
                    <button class="btn btn-small btn-warning" onclick="admin.editUser('${
                      user.id
                    }')">Edit</button>
                    <button class="btn btn-small btn-danger" onclick="admin.deleteUser('${
                      user.id
                    }')">Delete</button>
                </td>
            </tr>
        `
      )
      .join("");

    this.renderPagination();
  },

  // Render pagination
  renderPagination() {
    const totalPages = Math.ceil(this.filteredUsers.length / this.itemsPerPage);
    const pagination = document.getElementById("users-pagination");

    if (totalPages <= 1) {
      pagination.innerHTML = "";
      return;
    }

    let paginationHTML = "";
    for (let i = 1; i <= totalPages; i++) {
      paginationHTML += `<button class="page-btn ${
        i === this.currentPageNum ? "active" : ""
      }" onclick="admin.goToPage(${i})">${i}</button>`;
    }
    pagination.innerHTML = paginationHTML;
  },

  // Go to page
  goToPage(pageNum) {
    this.currentPageNum = pageNum;
    this.renderUsers();
  },

