                <label style="display: block; margin-bottom: 0.5rem;">
                    <input type="checkbox" value="${
                      user.id
                    }" style="margin-right: 0.5rem;">
                    ${user.full_name || user.email}
                </label>
            `
        )
        .join("");
    } catch (error) {
      console.error("Error loading user checkboxes:", error);
    }
