  // Log activity
  async logActivity(action, details) {
    try {
      // Prefer Firestore for activity logs
      if (window.firebase && window.firebase.firestore) {
        const db = window.firebase.firestore();
        await db.collection("activity_logs").add({
          admin_id: this.currentUser?.id || this.currentUser?.uid || null,
          action,
          details,
          created_at: new Date().toISOString(),
        });
        return;
      }

      // Fallback to Supabase
      if (window.supabase) {
        await supabase.from("activity_logs").insert({
          admin_id: this.currentUser?.id || this.currentUser?.uid || null,
          action: action,
          details: details,
          created_at: new Date().toISOString(),
        });
        return;
      }
    } catch (error) {
      console.error("Error logging activity:", error);
    }
  },

  // Utility functions
  getActivityIcon(action) {
    const icons = {
      "User Login": "sign-in-alt",
      "User Updated": "user-edit",
      "User Deleted": "user-times",
      "Broadcast Sent": "bullhorn",
      "Deposit Approved": "check-circle",
      "Withdrawal Processed": "money-check-alt",
    };
    return icons[action] || "circle";
  },

  formatTimeAgo(dateString) {
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMins / 60);
    const diffDays = Math.floor(diffHours / 24);

    if (diffMins < 1) return "Just now";
    if (diffMins < 60) return `${diffMins} minutes ago`;
    if (diffHours < 24) return `${diffHours} hours ago`;
    return `${diffDays} days ago`;
  },

  formatDate(dateString) {
    return new Date(dateString).toLocaleDateString();
  },

