  // Update dashboard statistics in real-time
  async updateDashboardStats() {
    try {
      const [usersResult, depositsResult, withdrawalsResult] =
        await Promise.all([
          supabase.from("profiles").select("id, created_at"),
          supabase.from("transactions").select("amount").eq("type", "deposit"),
          supabase.from("withdrawals").select("id").eq("status", "pending"),
        ]);

      if (usersResult.data) {
        document.getElementById("total-users").textContent =
          usersResult.data.length;
        const activeUsers = usersResult.data.filter((u) => {
          const lastLogin = new Date(u.last_login || u.created_at);
          const weekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
          return lastLogin > weekAgo;
        }).length;
        document.getElementById("active-users").textContent = activeUsers;
      }

      if (depositsResult.data) {
        const totalDeposits = depositsResult.data.reduce(
          (sum, t) => sum + (t.amount || 0),
          0
        );
        document.getElementById(
          "total-deposits"
        ).textContent = `${totalDeposits.toFixed(2)}`;
      }

      if (withdrawalsResult.data) {
        document.getElementById("pending-withdrawals").textContent =
          withdrawalsResult.data.length;
      }
    } catch (error) {
      console.error("Error updating dashboard stats:", error);
    }
  },

  // Admin control methods
  async approveDeposit(transactionId) {
    try {
      // Get transaction details
      const { data: transaction, error: fetchError } = await supabase
        .from("transactions")
        .select("*")
        .eq("id", transactionId)
        .single();

      if (fetchError) throw fetchError;

      // Use Firestore transaction when available for atomic update
      if (window.firebase && window.firebase.firestore) {
        const db = window.firebase.firestore();
        await db.runTransaction(async (tx) => {
          const txRef = db.collection("transactions").doc(transactionId);
          const txDoc = await tx.get(txRef);
          if (!txDoc.exists) throw new Error("Transaction not found");
          const txData = txDoc.data();
          if (txData.status === "completed") return; // already processed

          const userRef = db.collection("profiles").doc(transaction.user_id);
          const userDoc = await tx.get(userRef);
          if (!userDoc.exists) throw new Error("User profile not found");

          // Update transaction status
          tx.update(txRef, {
            status: "completed",
            updated_at: new Date().toISOString(),
          });

          // Update user balance
          const prevBalance = userDoc.data().balance || 0;
          const newBalance = prevBalance + (transaction.amount || 0);
          tx.update(userRef, {
            balance: newBalance,
            updated_at: new Date().toISOString(),
          });

          // Create an admin_log entry
          const logRef = db.collection("activity_logs").doc();
          tx.set(logRef, {
            admin_id: this.currentUser?.id || this.currentUser?.uid || null,
            action: "Deposit Approved",
            details: `Approved deposit of ${transaction.amount} for user ${transaction.user_id}`,
            created_at: new Date().toISOString(),
          });
        });
      } else {
        // Supabase fallback
        const { error: updateError } = await supabase
          .from("transactions")
          .update({ status: "completed", updated_at: new Date().toISOString() })
          .eq("id", transactionId);
        if (updateError) throw updateError;

        const { error: balanceError } = await supabase
          .from("profiles")
          .update({
            balance: supabase.sql`balance + ${transaction.amount}`,
            updated_at: new Date().toISOString(),
          })
          .eq("id", transaction.user_id);
        if (balanceError) throw balanceError;

        await this.logActivity(
          "Deposit Approved",
          `Approved deposit of ${transaction.amount} for user ${transaction.user_id}`
        );
      }

      showToast("Deposit approved successfully", "success");

      // Refresh data
      this.updateDashboardStats();
    } catch (error) {
      console.error("Error approving deposit:", error);
      showToast("Error approving deposit", "error");
    }
  },

  async rejectDeposit(transactionId) {
    try {
