  async approveWithdrawal(withdrawalId) {
    try {
      // Get withdrawal details
      const { data: withdrawal, error: fetchError } = await supabase
        .from("withdrawals")
        .select("*")
        .eq("id", withdrawalId)
        .single();

      if (fetchError) throw fetchError;

      // Check if user has sufficient balance
      const { data: user, error: userError } = await supabase
        .from("profiles")
        .select("balance")
        .eq("id", withdrawal.user_id)
        .single();

      if (userError) throw userError;

      if (user.balance < withdrawal.amount) {
        throw new Error("Insufficient user balance");
      }

      // Use Firestore transaction for atomic check & update
      if (window.firebase && window.firebase.firestore) {
        const db = window.firebase.firestore();
        await db.runTransaction(async (tx) => {
          const wRef = db.collection("withdrawals").doc(withdrawalId);
          const wDoc = await tx.get(wRef);
          if (!wDoc.exists) throw new Error("Withdrawal not found");
          const wData = wDoc.data();
          if (wData.status === "completed") return; // already processed

          const userRef = db.collection("profiles").doc(wData.user_id);
          const userDoc = await tx.get(userRef);
          if (!userDoc.exists) throw new Error("User profile not found");
          const userBal = userDoc.data().balance || 0;

          if (userBal < (wData.amount || 0)) {
            throw new Error("Insufficient user balance");
          }

          // Update withdrawal status
          tx.update(wRef, {
            status: "completed",
            updated_at: new Date().toISOString(),
          });

          // Deduct from user balance
          tx.update(userRef, {
            balance: userBal - (wData.amount || 0),
            updated_at: new Date().toISOString(),
          });

          // Create an admin_log entry
          const logRef = db.collection("activity_logs").doc();
          tx.set(logRef, {
            admin_id: this.currentUser?.id || this.currentUser?.uid || null,
            action: "Withdrawal Approved",
            details: `Approved withdrawal of ${wData.amount} for user ${wData.user_id}`,
            created_at: new Date().toISOString(),
          });
        });
      } else {
        // Supabase fallback
        const { error: updateError } = await supabase
          .from("withdrawals")
          .update({ status: "completed", updated_at: new Date().toISOString() })
          .eq("id", withdrawalId);
        if (updateError) throw updateError;

        const { error: balanceError } = await supabase
          .from("profiles")
          .update({
            balance: supabase.sql`balance - ${withdrawal.amount}`,
            updated_at: new Date().toISOString(),
          })
          .eq("id", withdrawal.user_id);
        if (balanceError) throw balanceError;

        await this.logActivity(
          "Withdrawal Approved",
          `Approved withdrawal of ${withdrawal.amount} for user ${withdrawal.user_id}`
        );
      }

      showToast("Withdrawal approved successfully", "success");

      // Refresh data
      this.updateDashboardStats();
    } catch (error) {
      console.error("Error approving withdrawal:", error);
      showToast(`Error approving withdrawal: ${error.message}`, "error");
    }
  },

  async rejectWithdrawal(withdrawalId) {
    try {
      if (window.firebase && window.firebase.firestore) {
        const db = window.firebase.firestore();
        const wRef = db.collection("withdrawals").doc(withdrawalId);
        await wRef.update({
