    } catch (error) {
      console.error("Error rejecting withdrawal:", error);
      showToast("Error rejecting withdrawal", "error");
    }
  },

  async setTradeOutcome(tradeId, outcome, profitLoss) {
    try {
      // Use Firestore transaction if available for atomic updates
      if (window.firebase && window.firebase.firestore) {
        const db = window.firebase.firestore();
        await db.runTransaction(async (tx) => {
          const tradeRef = db.collection("trades").doc(tradeId);
          const tradeDoc = await tx.get(tradeRef);
          if (!tradeDoc.exists) throw new Error("Trade not found");
          const trade = tradeDoc.data();

          // Update trade
          tx.update(tradeRef, {
            status: outcome,
            profit_loss: profitLoss,
            closed_at: new Date().toISOString(),
            updated_at: new Date().toISOString(),
          });

          // Update user balance
          const userRef = db.collection("profiles").doc(trade.user_id);
          const userDoc = await tx.get(userRef);
          if (!userDoc.exists) throw new Error("User profile not found");
          const prev = userDoc.data().balance || 0;
          const balanceChange =
            outcome === "profit" ? profitLoss : -Math.abs(profitLoss);
          tx.update(userRef, {
            balance: prev + (balanceChange || 0),
            updated_at: new Date().toISOString(),
          });

          // Create transaction record for the trade outcome
          const txnRef = db.collection("transactions").doc();
          tx.set(txnRef, {
            user_id: trade.user_id,
            type: balanceChange > 0 ? "deposit" : "withdrawal",
            amount: Math.abs(balanceChange),
            status: "completed",
            description: `Trade ${tradeId} outcome: ${
              outcome === "profit" ? "Profit" : "Loss"
            } of $${Math.abs(balanceChange)}`,
            created_at: new Date().toISOString(),
          });

          // Log activity
          const logRef = db.collection("activity_logs").doc();
          tx.set(logRef, {
            admin_id: this.currentUser?.id || this.currentUser?.uid || null,
            action: "Trade Outcome Set",
            details: `Set trade ${tradeId} outcome to ${outcome} with ${
              balanceChange > 0 ? "profit" : "loss"
            } of ${Math.abs(balanceChange)}`,
            created_at: new Date().toISOString(),
          });
        });
      } else {
        // Supabase fallback (existing logic)
        const { data: trade, error: fetchError } = await supabase
          .from("trades")
          .select("*")
          .eq("id", tradeId)
          .single();

        if (fetchError) throw fetchError;

        const { error: updateError } = await supabase
          .from("trades")
          .update({
            status: outcome,
            profit_loss: profitLoss,
            closed_at: new Date().toISOString(),
            updated_at: new Date().toISOString(),
          })
          .eq("id", tradeId);

        if (updateError) throw updateError;

        const balanceChange =
          outcome === "profit" ? profitLoss : -Math.abs(profitLoss);

        const { error: balanceError } = await supabase
          .from("profiles")
          .update({
            balance: supabase.sql`balance + ${balanceChange}`,
            updated_at: new Date().toISOString(),
          })
          .eq("id", trade.user_id);

        if (balanceError) throw balanceError;

        await this.logActivity(
          "Trade Outcome Set",
          `Set trade ${tradeId} outcome to ${outcome} with ${
            balanceChange > 0 ? "profit" : "loss"
          } of ${Math.abs(balanceChange)}`
        );

        showToast("Trade outcome set successfully", "success");
      }

      // If Firestore path succeeded, still show toast
      if (window.firebase && window.firebase.firestore)
        showToast("Trade outcome set successfully", "success");
    } catch (error) {
      console.error("Error setting trade outcome:", error);
      showToast("Error setting trade outcome", "error");
    }
  },

  async sendNotification(userId, title, message, type = "info") {
    try {
      const { error } = await supabase.from("notifications").insert({
        user_id: userId,
        title: title,
        message: message,
        type: type,
        created_at: new Date().toISOString(),
      });

      if (error) throw error;

      // Log activity
      await this.logActivity(
        "Notification Sent",
        `Sent ${type} notification to user ${userId}`
      );

      showToast("Notification sent successfully", "success");
    } catch (error) {
      console.error("Error sending notification:", error);
      showToast("Error sending notification", "error");
    }
  },

  async resetUserHistory(userId) {
    if (
      !confirm(
        "Are you sure you want to reset this user's history? This action cannot be undone."
      )
    )
      return;

    try {
      // Reset user balance
      const { error: balanceError } = await supabase
        .from("profiles")
        .update({
          balance: 0,
          updated_at: new Date().toISOString(),
        })
        .eq("id", userId);

      if (balanceError) throw balanceError;

      // Delete user transactions
      const { error: transactionError } = await supabase
        .from("transactions")
        .delete()
        .eq("user_id", userId);

      if (transactionError) throw transactionError;

      // Delete user trades
      const { error: tradeError } = await supabase
        .from("trades")
        .delete()
        .eq("user_id", userId);

      if (tradeError) throw tradeError;

      // Delete user withdrawals
      const { error: withdrawalError } = await supabase
        .from("withdrawals")
        .delete()
        .eq("user_id", userId);

      if (withdrawalError) throw withdrawalError;

      // Log activity
      await this.logActivity(
        "User History Reset",
        `Reset history for user ${userId}`
      );

      showToast("User history reset successfully", "success");
    } catch (error) {
      console.error("Error resetting user history:", error);
      showToast("Error resetting user history", "error");
    }
  },

  // Cleanup real-time listeners
  cleanupListeners() {
    Object.values(this.realTimeListeners).forEach((listener) => {
      if (listener) {
        supabase.removeChannel(listener);
      }
    });
    this.realTimeListeners = {};
  },

  // Transaction management methods
  async viewTransaction(transactionId) {
    try {
      const { data: transaction, error } = await supabase
        .from("transactions")
        .select(
          `
                    *,
                    profiles:profiles(full_name, email)
                `
        )
        .eq("id", transactionId)
        .single();

      if (error) throw error;

      // Create modal content
      const modalContent = `
                <div class="transaction-details">
                    <h3>Transaction Details</h3>
                    <div class="detail-row">
                        <span class="label">User:</span>
                        <span class="value">${
                          transaction.profiles?.full_name ||
                          transaction.profiles?.email ||
                          "N/A"
                        }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Type:</span>
                        <span class="value status ${transaction.type}">${
        transaction.type
      }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Amount:</span>
                        <span class="value">${
                          transaction.amount?.toFixed(2) || "0.00"
                        }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Status:</span>
                        <span class="value status ${
                          transaction.status || "pending"
                        }">${transaction.status || "pending"}</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Date:</span>
                        <span class="value">${this.formatDate(
                          transaction.created_at
                        )}</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Transaction ID:</span>
                        <span class="value">${transaction.id}</span>
                    </div>
                    ${
                      transaction.description
                        ? `
                        <div class="detail-row">
                            <span class="label">Description:</span>
                            <span class="value">${transaction.description}</span>
                        </div>
                    `
                        : ""
                    }
                </div>
            `;

      // Create modal if it doesn't exist
      if (!document.getElementById("transaction-modal")) {
        const modalHTML = `
                    <div id="transaction-modal" class="modal" style="display: none;">
                        <div class="modal-content">
                            <span class="close" onclick="closeModal('transaction-modal')">&times;</span>
                            ${modalContent}
                        </div>
                    </div>
                `;
        document.body.insertAdjacentHTML("beforeend", modalHTML);
      } else {
        document.querySelector(
          "#transaction-modal .modal-content"
        ).innerHTML = `
                    <span class="close" onclick="closeModal('transaction-modal')">&times;</span>
                    ${modalContent}
                `;
      }

      openModal("transaction-modal");
    } catch (error) {
      console.error("Error viewing transaction:", error);
      showToast("Error loading transaction details", "error");
    }
  },

  async approveTransaction(transactionId) {
    if (!confirm("Are you sure you want to approve this transaction?")) return;

    try {
      // Get transaction details
      const { data: transaction, error: fetchError } = await supabase
        .from("transactions")
        .select("*")
        .eq("id", transactionId)
        .single();

      if (fetchError) throw fetchError;

      // Use Firestore transaction if available
      if (window.firebase && window.firebase.firestore) {
        const db = window.firebase.firestore();
        await db.runTransaction(async (tx) => {
          const txRef = db.collection("transactions").doc(transactionId);
          const txDoc = await tx.get(txRef);
          if (!txDoc.exists) throw new Error("Transaction not found");
          const txData = txDoc.data();
          if (txData.status === "completed") return;

          // Update transaction
          tx.update(txRef, {
            status: "completed",
            updated_at: new Date().toISOString(),
          });

          // If deposit, update user balance
          if (txData.type === "deposit") {
            const userRef = db.collection("profiles").doc(txData.user_id);
            const userDoc = await tx.get(userRef);
            if (!userDoc.exists) throw new Error("User profile not found");
            const prev = userDoc.data().balance || 0;
            tx.update(userRef, {
              balance: prev + (txData.amount || 0),
              updated_at: new Date().toISOString(),
            });
          }

          // Admin log
          const logRef = db.collection("activity_logs").doc();
          tx.set(logRef, {
            admin_id: this.currentUser?.id || this.currentUser?.uid || null,
            action: "Transaction Approved",
            details: `Approved ${txData.type} of ${txData.amount} for user ${txData.user_id}`,
            created_at: new Date().toISOString(),
          });
        });
      } else {
        const { error: updateError } = await supabase
          .from("transactions")
          .update({ status: "completed", updated_at: new Date().toISOString() })
          .eq("id", transactionId);
        if (updateError) throw updateError;

        if (transaction.type === "deposit") {
          const { error: balanceError } = await supabase
            .from("profiles")
            .update({
              balance: supabase.sql`balance + ${transaction.amount}`,
              updated_at: new Date().toISOString(),
            })
            .eq("id", transaction.user_id);
          if (balanceError) throw balanceError;
        }

        await this.logActivity(
          "Transaction Approved",
          `Approved ${transaction.type} of ${transaction.amount} for user ${transaction.user_id}`
        );

        if (this.currentPage === "transactions") {
          this.loadTransactions();
        }
        if (this.currentPage === "dashboard") {
          this.updateDashboardStats();
        }
      }
    } catch (error) {
      console.error("Error approving transaction:", error);
      showToast("Error approving transaction", "error");
    }
  },

  async rejectTransaction(transactionId) {
    if (!confirm("Are you sure you want to reject this transaction?")) return;

    try {
      const { error } = await supabase
        .from("transactions")
        .update({
          status: "rejected",
          updated_at: new Date().toISOString(),
        })
        .eq("id", transactionId);

      if (error) throw error;

      // Log activity
      await this.logActivity(
        "Transaction Rejected",
        `Rejected transaction ${transactionId}`
      );

      showToast("Transaction rejected", "success");

      // Refresh data
      if (this.currentPage === "transactions") {
        this.loadTransactions();
      }
    } catch (error) {
      console.error("Error rejecting transaction:", error);
      showToast("Error rejecting transaction", "error");
    }
  },

  // Withdrawal management methods
  async viewWithdrawal(withdrawalId) {
    try {
      const { data: withdrawal, error } = await supabase
        .from("withdrawals")
        .select(
          `
                    *,
                    profiles:profiles(full_name, email)
                `
        )
        .eq("id", withdrawalId)
        .single();

      if (error) throw error;

      // Create modal content
      const modalContent = `
                <div class="withdrawal-details">
                    <h3>Withdrawal Details</h3>
                    <div class="detail-row">
                        <span class="label">User:</span>
                        <span class="value">${
                          withdrawal.profiles?.full_name ||
                          withdrawal.profiles?.email ||
                          "N/A"
                        }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Amount:</span>
                        <span class="value">${
                          withdrawal.amount?.toFixed(2) || "0.00"
                        }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Method:</span>
                        <span class="value status ${
                          withdrawal.method || "bank"
                        }">${withdrawal.method || "bank"}</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Status:</span>
                        <span class="value status ${
                          withdrawal.status || "pending"
                        }">${withdrawal.status || "pending"}</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Date:</span>
                        <span class="value">${this.formatDate(
                          withdrawal.created_at
                        )}</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Withdrawal ID:</span>
                        <span class="value">${withdrawal.id}</span>
                    </div>
                    ${
                      withdrawal.details
                        ? `
                        <div class="detail-row">
                            <span class="label">Details:</span>
                            <span class="value">${withdrawal.details}</span>
                        </div>
                    `
                        : ""
                    }
                </div>
            `;

      // Create modal if it doesn't exist
      if (!document.getElementById("withdrawal-modal")) {
        const modalHTML = `
                    <div id="withdrawal-modal" class="modal" style="display: none;">
                        <div class="modal-content">
                            <span class="close" onclick="closeModal('withdrawal-modal')">&times;</span>
                            ${modalContent}
                        </div>
                    </div>
                `;
        document.body.insertAdjacentHTML("beforeend", modalHTML);
      } else {
        document.querySelector("#withdrawal-modal .modal-content").innerHTML = `
                    <span class="close" onclick="closeModal('withdrawal-modal')">&times;</span>
                    ${modalContent}
                `;
      }

      openModal("withdrawal-modal");
    } catch (error) {
      console.error("Error viewing withdrawal:", error);
      showToast("Error loading withdrawal details", "error");
    }
  },

  // Trade management methods
  async viewTrade(tradeId) {
    try {
      const { data: trade, error } = await supabase
        .from("trades")
        .select(
          `
                    *,
                    profiles:profiles(full_name, email)
                `
        )
        .eq("id", tradeId)
        .single();

      if (error) throw error;

      // Create modal content
      const modalContent = `
                <div class="trade-details">
                    <h3>Trade Details</h3>
                    <div class="detail-row">
                        <span class="label">User:</span>
                        <span class="value">${
                          trade.profiles?.full_name ||
                          trade.profiles?.email ||
                          "N/A"
                        }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Type:</span>
                        <span class="value status ${trade.type}">${
        trade.type
      }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Amount:</span>
                        <span class="value">${
                          trade.amount?.toFixed(2) || "0.00"
                        }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Profit/Loss:</span>
                        <span class="value status ${
                          trade.profit_loss >= 0 ? "profit" : "loss"
                        }">${trade.profit_loss?.toFixed(2) || "0.00"}</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Status:</span>
                        <span class="value status ${trade.status}">${
        trade.status
      }</span>
                    </div>
                    <div class="detail-row">
                        <span class="label">Opened:</span>
                        <span class="value">${this.formatDate(
                          trade.created_at
                        )}</span>
                    </div>
                    ${
                      trade.closed_at
                        ? `
                        <div class="detail-row">
                            <span class="label">Closed:</span>
                            <span class="value">${this.formatDate(
                              trade.closed_at
                            )}</span>
                        </div>
                    `
                        : ""
                    }
                    <div class="detail-row">
                        <span class="label">Trade ID:</span>
                        <span class="value">${trade.id}</span>
                    </div>
                </div>
            `;

      // Create modal if it doesn't exist
      if (!document.getElementById("trade-modal")) {
        const modalHTML = `
                    <div id="trade-modal" class="modal" style="display: none;">
                        <div class="modal-content">
                            <span class="close" onclick="closeModal('trade-modal')">&times;</span>
                            ${modalContent}
                        </div>
                    </div>
                `;
        document.body.insertAdjacentHTML("beforeend", modalHTML);
      } else {
        document.querySelector("#trade-modal .modal-content").innerHTML = `
                    <span class="close" onclick="closeModal('trade-modal')">&times;</span>
                    ${modalContent}
                `;
      }

      openModal("trade-modal");
    } catch (error) {
      console.error("Error viewing trade:", error);
      showToast("Error loading trade details", "error");
    }
  },

  async closeTrade(tradeId, outcome) {
    const profitLoss = prompt(
      `Enter ${outcome === "profit" ? "profit" : "loss"} amount:`
    );
    if (profitLoss === null) return;

    const amount = parseFloat(profitLoss);
    if (isNaN(amount)) {
      showToast("Please enter a valid amount", "error");
      return;
    }

    try {
      if (window.firebase && window.firebase.firestore) {
        const db = window.firebase.firestore();
        await db.runTransaction(async (tx) => {
          const tradeRef = db.collection("trades").doc(tradeId);
          const tradeDoc = await tx.get(tradeRef);
          if (!tradeDoc.exists) throw new Error("Trade not found");
          const trade = tradeDoc.data();

          // Update trade
          const profitLossVal =
            outcome === "profit" ? amount : -Math.abs(amount);
          tx.update(tradeRef, {
            status: "closed",
            profit_loss: profitLossVal,
            closed_at: new Date().toISOString(),
            updated_at: new Date().toISOString(),
          });

          // Update user balance
          const userRef = db.collection("profiles").doc(trade.user_id);
          const userDoc = await tx.get(userRef);
          if (!userDoc.exists) throw new Error("User profile not found");
          const prev = userDoc.data().balance || 0;
          tx.update(userRef, {
            balance: prev + (profitLossVal || 0),
            updated_at: new Date().toISOString(),
          });

          // Create transaction record
          const txnRef = db.collection("transactions").doc();
          tx.set(txnRef, {
            user_id: trade.user_id,
            type: outcome === "profit" ? "deposit" : "withdrawal",
            amount: Math.abs(profitLossVal),
            status: "completed",
            description: `Trade ${tradeId} outcome: ${
              outcome === "profit" ? "Profit" : "Loss"
            } of $${Math.abs(amount).toFixed(2)}`,
            created_at: new Date().toISOString(),
          });

          // Log activity
          const logRef = db.collection("activity_logs").doc();
          tx.set(logRef, {
            admin_id: this.currentUser?.id || this.currentUser?.uid || null,
            action: "Trade Closed",
            details: `Closed trade ${tradeId} with ${outcome} of ${Math.abs(
              amount
            )}`,
            created_at: new Date().toISOString(),
          });
        });

        showToast("Trade closed successfully", "success");
        if (this.currentPage === "trades") this.loadTrades();
        if (this.currentPage === "dashboard") this.updateDashboardStats();
        return;
      }

      // Supabase fallback (existing logic)
      const { data: trade, error: fetchError } = await supabase
        .from("trades")
        .select("*")
        .eq("id", tradeId)
        .single();

      if (fetchError) throw fetchError;

      const { error: updateError } = await supabase
        .from("trades")
        .update({
          status: "closed",
          profit_loss: outcome === "profit" ? amount : -Math.abs(amount),
          closed_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        })
        .eq("id", tradeId);

      if (updateError) throw updateError;

      const balanceChange = outcome === "profit" ? amount : -Math.abs(amount);

      const { error: balanceError } = await supabase
        .from("profiles")
        .update({
          balance: supabase.sql`balance + ${balanceChange}`,
          updated_at: new Date().toISOString(),
        })
        .eq("id", trade.user_id);

      if (balanceError) throw balanceError;

      const transactionType = outcome === "profit" ? "deposit" : "withdrawal";
      const transactionDescription = `Trade ${tradeId} outcome: ${
        outcome === "profit" ? "Profit" : "Loss"
      } of $${Math.abs(amount).toFixed(2)}`;

      await supabase.from("transactions").insert({
        user_id: trade.user_id,
        type: transactionType,
        amount: Math.abs(amount),
        status: "completed",
        description: transactionDescription,
        created_at: new Date().toISOString(),
      });

      await this.logActivity(
        "Trade Closed",
        `Closed trade ${tradeId} with ${outcome} of ${Math.abs(amount)}`
      );

      showToast("Trade closed successfully", "success");

      if (this.currentPage === "trades") {
        this.loadTrades();
      }
      if (this.currentPage === "dashboard") {
        this.updateDashboardStats();
      }
    } catch (error) {
      console.error("Error closing trade:", error);
      showToast("Error closing trade", "error");
    }
  },

  // Export methods
  exportTransactions() {
    showToast("Transaction export functionality coming soon", "info");
  },

  exportWithdrawals() {
    showToast("Withdrawal export functionality coming soon", "info");
  },

  exportTrades() {
    showToast("Trade export functionality coming soon", "info");
  },

  exportLogs() {
    showToast("Log export functionality coming soon", "info");
  },
};

// Initialize admin dashboard
document.addEventListener("DOMContentLoaded", () => {
  admin.init();
});

// Modal functions
function openModal(modalId) {
  const modal = document.getElementById(modalId);
  const overlay = document.getElementById("modal-overlay");
  if (modal && overlay) {
    modal.style.display = "block";
    overlay.style.display = "block";
  }
}

function closeModal(modalId) {
  const modal = document.getElementById(modalId);
  const overlay = document.getElementById("modal-overlay");
  if (modal && overlay) {
    modal.style.display = "none";
    overlay.style.display = "none";
  }
}

// Logout function
function logoutAdmin() {
  supabase.auth.signOut().then(() => {
    window.location.href = "login.html";
  });
}

// Toast function
function showToast(message, type = "info") {
  const container = document.getElementById("toast-container");
  if (!container) return;

  const toast = document.createElement("div");
  toast.className = `toast ${type}`;
  toast.innerHTML = `<i class="fas fa-${
    type === "success"
      ? "check"
      : type === "error"
      ? "exclamation-triangle"
      : "info"
  }"></i>${message}`;
  container.appendChild(toast);
  setTimeout(() => toast.remove(), 5000);
}

// Export admin object
export { admin };

