// server.js - Complete Backend Authentication Server
const express = require("express");
const cors = require("cors");
const nodemailer = require("nodemailer");
const crypto = require("crypto");
const bcrypt = require("bcryptjs");
const jwt = require("jsonwebtoken");
const admin = require("firebase-admin");
const path = require("path");
require("dotenv").config();

const app = express();
const PORT = process.env.PORT || 3000;

// Middleware
app.use(cors());
app.use(express.json());
app.use(express.static("public")); // Serve frontend files

// Initialize Firebase Admin
const serviceAccount = require("./serviceAccountKey.json");
admin.initializeApp({
  credential: admin.credential.cert(serviceAccount),
});
const db = admin.firestore();

// JWT Secret
const JWT_SECRET = process.env.JWT_SECRET || "024f236b2bf7653d";

// Email transporter
const transporter = nodemailer.createTransport({
  service: "gmail", // or your email service
  auth: {
    user: process.env.EMAIL_USER,
    pass: process.env.EMAIL_PASS,
  },
});

// Store verification tokens temporarily (use Redis in production)
const verificationTokens = new Map();
const passwordResetTokens = new Map();

// Helper function to generate tokens
function generateToken() {
  return crypto.randomBytes(32).toString("hex");
}

// Helper function to send verification email
async function sendVerificationEmail(email, token) {
  const verificationUrl = `http://localhost:${PORT}/api/auth/verify-email?token=${token}`;

  const mailOptions = {
    from: process.env.EMAIL_USER,
    to: email,
    subject: "Verify Your Email - Top-Margin Trading",
    html: `
      <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
        <h2>Welcome to Top-Margin Trading!</h2>
        <p>Thank you for signing up. Please verify your email address by clicking the button below:</p>
        <a href="${verificationUrl}" 
           style="display: inline-block; padding: 12px 24px; background-color: #4CAF50; color: white; 
                  text-decoration: none; border-radius: 4px; margin: 20px 0;">
          Verify Email
        </a>
        <p>Or copy and paste this link into your browser:</p>
        <p style="color: #666;">${verificationUrl}</p>
        <p>This link will expire in 24 hours.</p>
        <p>If you didn't create an account, please ignore this email.</p>
      </div>
    `,
  };

  await transporter.sendMail(mailOptions);
}

// Helper function to send password reset email
async function sendPasswordResetEmail(email, token) {
  const resetUrl = `http://localhost:${PORT}/reset-password.html?token=${token}`;

  const mailOptions = {
    from: process.env.EMAIL_USER,
    to: email,
    subject: "Reset Your Password - Top-Margin Trading",
    html: `
      <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
        <h2>Password Reset Request</h2>
        <p>We received a request to reset your password. Click the button below to proceed:</p>
        <a href="${resetUrl}" 
           style="display: inline-block; padding: 12px 24px; background-color: #2196F3; color: white; 
                  text-decoration: none; border-radius: 4px; margin: 20px 0;">
          Reset Password
        </a>
        <p>Or copy and paste this link into your browser:</p>
        <p style="color: #666;">${resetUrl}</p>
        <p>This link will expire in 1 hour.</p>
        <p>If you didn't request a password reset, please ignore this email.</p>
      </div>
    `,
  };

  await transporter.sendMail(mailOptions);
}

// Middleware to verify JWT token
function authenticateToken(req, res, next) {
  const authHeader = req.headers["authorization"];
  const token = authHeader && authHeader.split(" ")[1];

  if (!token) {
    return res.status(401).json({ error: "Access token required" });
  }

  jwt.verify(token, JWT_SECRET, (err, user) => {
    if (err) {
      return res.status(403).json({ error: "Invalid or expired token" });
    }
    req.user = user;
    next();
  });
}

// ============= AUTH ROUTES =============

// Register new user
app.post("/api/auth/register", async (req, res) => {
  try {
    const { email, password, fullName } = req.body;

    // Validate input
    if (!email || !password || !fullName) {
      return res.status(400).json({ error: "All fields are required" });
    }

    if (password.length < 8) {
      return res
        .status(400)
        .json({ error: "Password must be at least 8 characters" });
    }

    // Check if user already exists
    const userQuery = await db
      .collection("users")
      .where("email", "==", email.toLowerCase())
      .get();
    if (!userQuery.empty) {
      return res.status(400).json({ error: "Email already registered" });
    }

    // Hash password
    const hashedPassword = await bcrypt.hash(password, 10);

    // Create user in Firestore
    const userRef = await db.collection("users").add({
      email: email.toLowerCase(),
      password: hashedPassword,
      full_name: fullName,
      email_verified: false,
      created_at: admin.firestore.FieldValue.serverTimestamp(),
      updated_at: admin.firestore.FieldValue.serverTimestamp(),
      role: "user",
      status: "active",
    });

    // Create user profile
    await db
      .collection("profiles")
      .doc(userRef.id)
      .set({
        user_id: userRef.id,
        full_name: fullName,
        email: email.toLowerCase(),
        role: "user",
        balances: {
          USD: 0,
          BTC: 0,
          ETH: 0,
        },
        created_at: admin.firestore.FieldValue.serverTimestamp(),
      });

    // Generate verification token
    const verificationToken = generateToken();
    verificationTokens.set(verificationToken, {
      userId: userRef.id,
      email: email.toLowerCase(),
      expires: Date.now() + 24 * 60 * 60 * 1000, // 24 hours
    });

    // Send verification email
    await sendVerificationEmail(email, verificationToken);

    res.status(201).json({
      success: true,
      message:
        "Registration successful. Please check your email to verify your account.",
      userId: userRef.id,
    });
  } catch (error) {
    console.error("Registration error:", error);
    res.status(500).json({ error: "Registration failed. Please try again." });
  }
});

// Verify email
app.get("/api/auth/verify-email", async (req, res) => {
  try {
    const { token } = req.query;

    if (!token) {
      return res.status(400).send("Invalid verification link");
    }

    const tokenData = verificationTokens.get(token);
    if (!tokenData) {
      return res.status(400).send("Invalid or expired verification link");
    }

    if (Date.now() > tokenData.expires) {
      verificationTokens.delete(token);
      return res.status(400).send("Verification link has expired");
    }

    // Update user as verified
    await db.collection("users").doc(tokenData.userId).update({
      email_verified: true,
      updated_at: admin.firestore.FieldValue.serverTimestamp(),
    });

    // Clean up token
    verificationTokens.delete(token);

    // Redirect to success page
    res.redirect("/verify-success.html");
  } catch (error) {
    console.error("Email verification error:", error);
    res.status(500).send("Verification failed");
  }
});

// Resend verification email
app.post("/api/auth/resend-verification", async (req, res) => {
  try {
    const { email } = req.body;

    if (!email) {
      return res.status(400).json({ error: "Email is required" });
    }

    // Find user
    const userQuery = await db
      .collection("users")
      .where("email", "==", email.toLowerCase())
      .get();
    if (userQuery.empty) {
      return res.status(404).json({ error: "User not found" });
    }

    const userDoc = userQuery.docs[0];
    const userData = userDoc.data();

    if (userData.email_verified) {
      return res.status(400).json({ error: "Email already verified" });
    }

    // Generate new verification token
    const verificationToken = generateToken();
    verificationTokens.set(verificationToken, {
      userId: userDoc.id,
      email: email.toLowerCase(),
      expires: Date.now() + 24 * 60 * 60 * 1000,
    });

    // Send verification email
    await sendVerificationEmail(email, verificationToken);

    res.json({ success: true, message: "Verification email sent" });
  } catch (error) {
    console.error("Resend verification error:", error);
    res.status(500).json({ error: "Failed to resend verification email" });
  }
});

// Login
app.post("/api/auth/login", async (req, res) => {
  try {
    const { email, password } = req.body;

    if (!email || !password) {
      return res.status(400).json({ error: "Email and password are required" });
    }

    // Find user
    const userQuery = await db
      .collection("users")
      .where("email", "==", email.toLowerCase())
      .get();
    if (userQuery.empty) {
      return res.status(401).json({ error: "Invalid email or password" });
    }

    const userDoc = userQuery.docs[0];
    const userData = userDoc.data();

    // Check if email is verified
    if (!userData.email_verified) {
      return res.status(403).json({
        error: "Email not verified. Please check your inbox.",
        needsVerification: true,
        email: email.toLowerCase(),
      });
    }

    // Verify password
    const isValidPassword = await bcrypt.compare(password, userData.password);
    if (!isValidPassword) {
      return res.status(401).json({ error: "Invalid email or password" });
    }

    // Get user profile
    const profileDoc = await db.collection("profiles").doc(userDoc.id).get();
    const profileData = profileDoc.data();

    // Generate JWT token
    const token = jwt.sign(
      {
        userId: userDoc.id,
        email: userData.email,
        role: userData.role,
      },
      JWT_SECRET,
      { expiresIn: "7d" }
    );

    res.json({
      success: true,
      token,
      user: {
        id: userDoc.id,
        email: userData.email,
        full_name: userData.full_name,
        role: userData.role,
        email_verified: userData.email_verified,
      },
      profile: profileData,
    });
  } catch (error) {
    console.error("Login error:", error);
    res.status(500).json({ error: "Login failed. Please try again." });
  }
});

// Forgot password
app.post("/api/auth/forgot-password", async (req, res) => {
  try {
    const { email } = req.body;

    if (!email) {
      return res.status(400).json({ error: "Email is required" });
    }

    // Find user
    const userQuery = await db
      .collection("users")
      .where("email", "==", email.toLowerCase())
      .get();
    if (userQuery.empty) {
      // Don't reveal if email exists
      return res.json({
        success: true,
        message: "If the email exists, a reset link has been sent",
      });
    }

    const userDoc = userQuery.docs[0];

    // Generate reset token
    const resetToken = generateToken();
    passwordResetTokens.set(resetToken, {
      userId: userDoc.id,
      email: email.toLowerCase(),
      expires: Date.now() + 60 * 60 * 1000, // 1 hour
    });

    // Send password reset email
    await sendPasswordResetEmail(email, resetToken);

    res.json({ success: true, message: "Password reset email sent" });
  } catch (error) {
    console.error("Forgot password error:", error);
    res.status(500).json({ error: "Failed to process request" });
  }
});

// Reset password
app.post("/api/auth/reset-password", async (req, res) => {
  try {
    const { token, newPassword } = req.body;

    if (!token || !newPassword) {
      return res
        .status(400)
        .json({ error: "Token and new password are required" });
    }

    if (newPassword.length < 8) {
      return res
        .status(400)
        .json({ error: "Password must be at least 8 characters" });
    }

    const tokenData = passwordResetTokens.get(token);
    if (!tokenData) {
      return res.status(400).json({ error: "Invalid or expired reset link" });
    }

    if (Date.now() > tokenData.expires) {
      passwordResetTokens.delete(token);
      return res.status(400).json({ error: "Reset link has expired" });
    }

    // Hash new password
    const hashedPassword = await bcrypt.hash(newPassword, 10);

    // Update user password
    await db.collection("users").doc(tokenData.userId).update({
      password: hashedPassword,
      updated_at: admin.firestore.FieldValue.serverTimestamp(),
    });

    // Clean up token
    passwordResetTokens.delete(token);

    res.json({ success: true, message: "Password reset successful" });
  } catch (error) {
    console.error("Reset password error:", error);
    res.status(500).json({ error: "Failed to reset password" });
  }
});

// Get current user (protected route)
app.get("/api/auth/me", authenticateToken, async (req, res) => {
  try {
    const userDoc = await db.collection("users").doc(req.user.userId).get();
    if (!userDoc.exists) {
      return res.status(404).json({ error: "User not found" });
    }

    const userData = userDoc.data();
    const profileDoc = await db
      .collection("profiles")
      .doc(req.user.userId)
      .get();
    const profileData = profileDoc.data();

    res.json({
      user: {
        id: userDoc.id,
        email: userData.email,
        full_name: userData.full_name,
        role: userData.role,
        email_verified: userData.email_verified,
      },
      profile: profileData,
    });
  } catch (error) {
    console.error("Get user error:", error);
    res.status(500).json({ error: "Failed to get user data" });
  }
});

// Logout (client-side handles token removal, but we can blacklist if needed)
app.post("/api/auth/logout", authenticateToken, (req, res) => {
  res.json({ success: true, message: "Logged out successfully" });
});

// ============= SERVE FRONTEND =============

// Serve frontend pages
app.get("/", (req, res) => {
  res.sendFile(path.join(__dirname, "public", "index.html"));
});

app.get("/signup", (req, res) => {
  res.sendFile(path.join(__dirname, "public", "signup.html"));
});

app.get("/login", (req, res) => {
  res.sendFile(path.join(__dirname, "public", "login.html"));
});

app.get("/dashboard", (req, res) => {
  res.sendFile(path.join(__dirname, "public", "dashboard.html"));
});

app.get("/verify-success.html", (req, res) => {
  res.send(`
    <!DOCTYPE html>
    <html>
    <head>
      <title>Email Verified</title>
      <style>
        body { font-family: Arial, sans-serif; text-align: center; padding: 50px; }
        .success { color: #4CAF50; font-size: 24px; margin: 20px 0; }
        .btn { 
          display: inline-block; padding: 12px 24px; background: #4CAF50; 
          color: white; text-decoration: none; border-radius: 4px; margin-top: 20px;
        }
      </style>
    </head>
    <body>
      <h1>✅ Email Verified!</h1>
      <p class="success">Your email has been successfully verified.</p>
      <p>You can now log in to your account.</p>
      <a href="/login" class="btn">Go to Login</a>
    </body>
    </html>
  `);
});

// Start server
app.listen(PORT, () => {
  console.log(`🚀 Server running on http://localhost:${PORT}`);
  console.log(
    `📧 Email service: ${
      process.env.EMAIL_USER ? "Configured" : "NOT CONFIGURED"
    }`
  );
});

public/css/admin-bottom-nav.css.content:
/* Majestic Bottom Navigation Styles for Admin Panel */

:root {
  --nav-primary: #6366f1;
  --nav-secondary: #8b5cf6;
  --nav-tertiary: #ec4899;
  --nav-success: #10b981;
  --nav-warning: #f59e0b;
  --nav-danger: #ef4444;
  --nav-info: #06b6d4;
  --nav-surface: rgba(255, 255, 255, 0.1);
  --nav-surface-hover: rgba(255, 255, 255, 0.15);
  --nav-border: rgba(255, 255, 255, 0.2);
  --nav-border-hover: rgba(255, 255, 255, 0.3);
  --nav-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
  --nav-shadow-hover: 0 16px 64px rgba(0, 0, 0, 0.6);
  --nav-glow: 0 0 20px rgba(99, 102, 241, 0.4);
  --nav-glow-hover: 0 0 30px rgba(99, 102, 241, 0.6);
  --nav-gradient: linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%);
  --nav-gradient-hover: linear-gradient(135deg, #764ba2 0%, #f093fb 50%, #f5576c 100%);
}

/* Bottom Navigation */
.admin-bottom-nav {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(135deg, rgba(26, 26, 26, 0.95) 0%, rgba(15, 15, 15, 0.98) 100%);
  border-top: 1px solid var(--nav-border);
  display: flex;
  justify-content: space-around;
  align-items: center;
  padding: 1rem 0;
  z-index: 1000;
  backdrop-filter: blur(20px);
  box-shadow: var(--nav-shadow);
  border-top: 1px solid transparent;
  background-image: linear-gradient(90deg, transparent, rgba(99, 102, 241, 0.1), transparent);
}

[data-theme="dark"] .admin-bottom-nav {
  background: linear-gradient(135deg, rgba(26, 26, 26, 0.98) 0%, rgba(15, 15, 15, 0.99) 100%);
  border-top: 1px solid var(--nav-border);
}

.admin-bottom-nav::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 1px;
  background: linear-gradient(90deg, transparent 0%, var(--nav-primary) 50%, transparent 100%);
  opacity: 0.6;
}

.admin-bottom-nav .nav-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 0.75rem;
  border-radius: 16px;
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  min-width: 70px;
  min-height: 70px;
  color: rgba(255, 255, 255, 0.7);
  text-decoration: none;
  position: relative;
  transform-style: preserve-3d;
  backdrop-filter: blur(10px);
  border: 1px solid transparent;
}

.admin-bottom-nav .nav-item::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(135deg, transparent 0%, rgba(255, 255, 255, 0.05) 50%, transparent 100%);
  border-radius: 16px;
  opacity: 0;
  transition: opacity 0.3s ease;
  z-index: -1;
}

.admin-bottom-nav .nav-item:hover::before {
  opacity: 1;
}

.admin-bottom-nav .nav-item:hover {
  color: white;
  transform: translateY(-8px) translateZ(20px) scale(1.05);
  background: linear-gradient(135deg, rgba(99, 102, 241, 0.2) 0%, rgba(139, 92, 246, 0.2) 100%);
  border-color: var(--nav-border-hover);
  box-shadow: var(--nav-glow-hover);
}

.admin-bottom-nav .nav-item:hover i {
  animation: icon-bounce 0.6s ease-in-out;
}

@keyframes icon-bounce {
  0%, 100% { transform: translateY(0) scale(1); }
  50% { transform: translateY(-5px) scale(1.1); }
}

.admin-bottom-nav .nav-item.active {
  color: white;
  background: linear-gradient(135deg, var(--nav-primary) 0%, var(--nav-secondary) 100%);
  border-color: var(--nav-border-hover);
  box-shadow: var(--nav-glow);
  transform: translateY(-5px) translateZ(15px);
}

.admin-bottom-nav .nav-item.active::after {
  content: '';
  position: absolute;
  bottom: -5px;
  left: 50%;
  transform: translateX(-50%);
  width: 30px;
  height: 3px;
  background: linear-gradient(90deg, var(--nav-primary), var(--nav-secondary));
  border-radius: 2px;
  animation: active-glow 2s ease-in-out infinite alternate;
}

@keyframes active-glow {
  0% { box-shadow: 0 0 5px rgba(99, 102, 241, 0.5); }
  100% { box-shadow: 0 0 15px rgba(99, 102, 241, 0.8); }
}

[data-theme="dark"] .admin-bottom-nav .nav-item.active {
  background: linear-gradient(135deg, var(--nav-primary) 0%, var(--nav-secondary) 100%);
  box-shadow: var(--nav-glow);
}

.admin-bottom-nav .nav-item i {
  font-size: 1.5rem;
  margin-bottom: 0.25rem;
  transition: all 0.3s ease;
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3));
}

.admin-bottom-nav .nav-item span {
  font-size: 0.75rem;
  font-weight: 600;
  text-align: center;
  line-height: 1.2;
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
  transition: all 0.3s ease;
}

.admin-bottom-nav .nav-item.nav-logout {
  color: var(--nav-danger);
}

.admin-bottom-nav .nav-item.nav-logout:hover {
  background: linear-gradient(135deg, rgba(239, 68, 68, 0.2) 0%, rgba(220, 38, 38, 0.2) 100%);
  color: var(--nav-danger);
  box-shadow: 0 0 20px rgba(239, 68, 68, 0.4);
}

.admin-bottom-nav .nav-item.nav-logout.active {
  background: linear-gradient(135deg, var(--nav-danger) 0%, #dc2626 100%);
  color: white;
}

/* Notification badges */
.nav-item .badge {
  position: absolute;
  top: 5px;
  right: 5px;
  background: var(--nav-danger);
  color: white;
  border-radius: 50%;
  width: 18px;
  height: 18px;
  font-size: 0.6rem;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  animation: badge-pulse 2s ease-in-out infinite;
}

@keyframes badge-pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.1); }
}

/* Adjust main content to account for bottom navigation */
.admin-main {
  margin-bottom: 100px; /* Space for bottom navigation */
  min-height: calc(100vh - 100px);
}

/* Mobile Responsive for Bottom Navigation */
@media (max-width: 768px) {
  .admin-bottom-nav {
    padding: 0.75rem 0;
    background: linear-gradient(135deg, rgba(26, 26, 26, 0.98) 0%, rgba(15, 15, 15, 0.99) 100%);
  }

  .admin-bottom-nav .nav-item {
    min-width: 60px;
    min-height: 60px;
    padding: 0.5rem;
    border-radius: 12px;
  }

  .admin-bottom-nav .nav-item i {
    font-size: 1.25rem;
    margin-bottom: 0.2rem;
  }

  .admin-bottom-nav .nav-item span {
    font-size: 0.65rem;
  }

  .admin-main {
    margin-bottom: 90px;
  }
}

@media (max-width: 480px) {
  .admin-bottom-nav .nav-item span {
    display: none; /* Hide text on very small screens, show only icons */
  }

  .admin-bottom-nav .nav-item {
    min-width: 50px;
    min-height: 50px;
    border-radius: 10px;
  }

  .admin-bottom-nav .nav-item i {
    font-size: 1.1rem;
  }

  .admin-main {
    margin-bottom: 80px;
  }
}

/* Enhanced 3D Effects */
.admin-bottom-nav .nav-item {
  perspective: 1000px;
}

.admin-bottom-nav .nav-item:hover {
  animation: nav-item-float 0.6s ease-in-out;
}

@keyframes nav-item-float {
  0% { transform: translateY(0) rotateX(0deg); }
  50% { transform: translateY(-10px) rotateX(5deg); }
  100% { transform: translateY(-8px) rotateX(0deg); }
}

/* Loading state for navigation */
.admin-bottom-nav.loading .nav-item {
  opacity: 0.6;
  pointer-events: none;
}

.admin-bottom-nav.loading .nav-item::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 20px;
  height: 20px;
  border: 2px solid transparent;
  border-top: 2px solid var(--nav-primary);
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: translate(-50%, -50%) rotate(0deg); }
  100% { transform: translate(-50%, -50%) rotate(360deg); }
}

public/css/base.css.content:
/* Base CSS - Global styles and utilities */

:root {
  /* Color System */
  --primary-100: #e3f2fd;
  --primary-200: #bbdefb;
  --primary-300: #90caf9;
  --primary-400: #64b5f6;
  --primary-500: #2196f3;
  --primary-600: #1976d2;
  --primary-700: #1565c0;
  --primary-800: #0d47a1;
  --primary-900: #0a3983;

  --secondary-100: #f3e5f5;
  --secondary-200: #e1bee7;
  --secondary-300: #ce93d8;
  --secondary-400: #ba68c8;
  --secondary-500: #9c27b0;
  --secondary-600: #8e24aa;
  --secondary-700: #7b1fa2;
  --secondary-800: #6a1b9a;
  --secondary-900: #4a148c;

  --success-100: #e8f5e8;
  --success-200: #c8e6c9;
  --success-300: #a5d6a7;
  --success-400: #81c784;
  --success-500: #4caf50;
  --success-600: #43a047;
  --success-700: #388e3c;
  --success-800: #2e7d32;
  --success-900: #1b5e20;

  --warning-100: #fff8e1;
  --warning-200: #ffecb3;
  --warning-300: #ffe082;
  --warning-400: #ffd54f;
  --warning-500: #ffc107;
  --warning-600: #ffb300;
  --warning-700: #ffa000;
  --warning-800: #ff8f00;
  --warning-900: #ff6f00;

  --error-100: #ffebee;
  --error-200: #ffcdd2;
  --error-300: #ef9a9a;
  --error-400: #e57373;
  --error-500: #f44336;
  --error-600: #e53935;
  --error-700: #d32f2f;
  --error-800: #c62828;
  --error-900: #b71c1c;

  --neutral-50: #fafafa;
  --neutral-100: #f5f5f5;
  --neutral-200: #eeeeee;
  --neutral-300: #e0e0e0;
  --neutral-400: #bdbdbd;
  --neutral-500: #9e9e9e;
  --neutral-600: #757575;
  --neutral-700: #616161;
  --neutral-800: #424242;
  --neutral-900: #212121;

  /* Typography */
  --font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  --font-size-xs: 0.75rem;
  --font-size-sm: 0.875rem;
  --font-size-base: 1rem;
  --font-size-lg: 1.125rem;
  --font-size-xl: 1.25rem;
  --font-size-2xl: 1.5rem;
  --font-size-3xl: 1.875rem;
  --font-size-4xl: 2.25rem;

  --line-height-tight: 1.25;
  --line-height-normal: 1.5;
  --line-height-relaxed: 1.625;

  /* Spacing */
  --spacing-1: 0.25rem;
  --spacing-2: 0.5rem;
  --spacing-3: 0.75rem;
  --spacing-4: 1rem;
  --spacing-5: 1.25rem;
  --spacing-6: 1.5rem;
  --spacing-8: 2rem;
  --spacing-10: 2.5rem;
  --spacing-12: 3rem;
  --spacing-16: 4rem;
  --spacing-20: 5rem;

  /* Borders */
  --border-radius-sm: 0.375rem;
  --border-radius-md: 0.5rem;
  --border-radius-lg: 0.75rem;
  --border-radius-xl: 1rem;
  --border-radius-2xl: 1.5rem;
  --border-radius-full: 9999px;

  /* Shadows */
  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  --shadow-2xl: 0 25px 50px -12px rgba(0, 0, 0, 0.25);

  /* Transitions */
  --transition-fast: 150ms cubic-bezier(0.4, 0, 0.2, 1);
  --transition-normal: 300ms cubic-bezier(0.4, 0, 0.2, 1);
  --transition-slow: 500ms cubic-bezier(0.4, 0, 0.2, 1);

  /* Z-index */
  --z-dropdown: 1000;
  --z-sticky: 1020;
  --z-fixed: 1030;
  --z-modal-backdrop: 1040;
  --z-modal: 1050;
  --z-popover: 1060;
  --z-tooltip: 1070;
  --z-toast: 1080;

  /* Light Theme Colors */
  --bg-primary: var(--neutral-50);
  --bg-secondary: #ffffff;
  --bg-tertiary: var(--neutral-100);
  --bg-hover: var(--neutral-200);
  --bg-selected: var(--primary-100);

  --text-primary: var(--neutral-900);
  --text-secondary: var(--neutral-700);
  --text-tertiary: var(--neutral-500);
  --text-inverse: #ffffff;

  --border-primary: var(--neutral-300);
  --border-secondary: var(--neutral-200);
  --border-focus: var(--primary-500);

  --surface: #ffffff;
  --surface-elevated: #ffffff;
}

[data-theme="dark"] {
  /* Dark Theme Colors */
  --bg-primary: #0a0a0a;
  --bg-secondary: #111111;
  --bg-tertiary: #1a1a1a;
  --bg-hover: #262626;
  --bg-selected: #1e3a8a;

  --text-primary: #ffffff;
  --text-secondary: #e5e5e5;
  --text-tertiary: #a3a3a3;
  --text-inverse: var(--neutral-900);

  --border-primary: #404040;
  --border-secondary: #262626;
  --border-focus: var(--primary-400);

  --surface: #1a1a1a;
  --surface-elevated: #262626;

  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.3);
  --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -1px rgba(0, 0, 0, 0.2);
  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.3), 0 4px 6px -2px rgba(0, 0, 0, 0.2);
  --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.4), 0 10px 10px -5px rgba(0, 0, 0, 0.2);
  --shadow-2xl: 0 25px 50px -12px rgba(0, 0, 0, 0.5);
}

/* Reset and Base Styles */
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

*::before,
*::after {
  box-sizing: border-box;
}

html {
  font-size: 16px;
  line-height: 1.5;
  -webkit-text-size-adjust: 100%;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-family: var(--font-family);
  font-size: var(--font-size-base);
  line-height: var(--line-height-normal);
  background-color: var(--bg-primary);
  color: var(--text-primary);
  transition: background-color var(--transition-normal), color var(--transition-normal);
  min-height: 100vh;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
  font-weight: 600;
  line-height: var(--line-height-tight);
  margin-bottom: var(--spacing-4);
}

h1 { font-size: var(--font-size-4xl); }
h2 { font-size: var(--font-size-3xl); }
h3 { font-size: var(--font-size-2xl); }
h4 { font-size: var(--font-size-xl); }
h5 { font-size: var(--font-size-lg); }
h6 { font-size: var(--font-size-base); }

p {
  margin-bottom: var(--spacing-4);
  line-height: var(--line-height-relaxed);
}

a {
  color: var(--primary-600);
  text-decoration: none;
  transition: color var(--transition-fast);
}

a:hover {
  color: var(--primary-700);
}

[data-theme="dark"] a {
  color: var(--primary-400);
}

[data-theme="dark"] a:hover {
  color: var(--primary-300);
}

/* Button Styles */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: var(--spacing-2);
  padding: var(--spacing-2) var(--spacing-4);
  border: 1px solid transparent;
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-sm);
  font-weight: 500;
  text-align: center;
  white-space: nowrap;
  background-color: transparent;
  color: inherit;
  cursor: pointer;
  transition: all var(--transition-fast);
  text-decoration: none;
  user-select: none;
}

.btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  pointer-events: none;
}

.btn-primary {
  background-color: var(--primary-600);
  color: white;
  border-color: var(--primary-600);
}

.btn-primary:hover:not(:disabled) {
  background-color: var(--primary-700);
  border-color: var(--primary-700);
  transform: translateY(-1px);
  box-shadow: var(--shadow-md);
}

.btn-outline {
  border-color: var(--border-primary);
  color: var(--text-primary);
}

.btn-outline:hover:not(:disabled) {
  background-color: var(--bg-hover);
  border-color: var(--primary-500);
  color: var(--primary-600);
}

[data-theme="dark"] .btn-outline:hover:not(:disabled) {
  color: var(--primary-400);
}

.btn-success {
  background-color: var(--success-600);
  color: white;
  border-color: var(--success-600);
}

.btn-success:hover:not(:disabled) {
  background-color: var(--success-700);
  border-color: var(--success-700);
}

.btn-danger {
  background-color: var(--error-600);
  color: white;
  border-color: var(--error-600);
}

.btn-danger:hover:not(:disabled) {
  background-color: var(--error-700);
  border-color: var(--error-700);
}

.btn-warning {
  background-color: var(--warning-600);
  color: var(--neutral-900);
  border-color: var(--warning-600);
}

.btn-warning:hover:not(:disabled) {
  background-color: var(--warning-700);
  border-color: var(--warning-700);
}

.btn-small {
  padding: var(--spacing-1) var(--spacing-3);
  font-size: var(--font-size-xs);
}

.btn-large {
  padding: var(--spacing-4) var(--spacing-8);
  font-size: var(--font-size-lg);
  border-radius: var(--border-radius-lg);
}

/* Form Elements */
.form-group {
  margin-bottom: var(--spacing-6);
}

.form-group label {
  display: block;
  margin-bottom: var(--spacing-2);
  font-size: var(--font-size-sm);
  font-weight: 500;
  color: var(--text-secondary);
}

.form-control,
input[type="text"],
input[type="email"],
input[type="password"],
input[type="number"],
input[type="tel"],
input[type="url"],
input[type="search"],
textarea,
select {
  width: 100%;
  padding: var(--spacing-3) var(--spacing-4);
  border: 1px solid var(--border-primary);
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-base);
  background-color: var(--surface);
  color: var(--text-primary);
  transition: all var(--transition-fast);
}

.form-control:focus,
input:focus,
textarea:focus,
select:focus {
  outline: none;
  border-color: var(--border-focus);
  box-shadow: 0 0 0 3px rgba(33, 150, 243, 0.1);
}

.form-control.error,
input.error,
textarea.error,
select.error {
  border-color: var(--error-500);
}

.error-message {
  margin-top: var(--spacing-1);
  font-size: var(--font-size-xs);
  color: var(--error-600);
  opacity: 0;
  transition: opacity var(--transition-fast);
}

.error-message:not(:empty) {
  opacity: 1;
}

/* Input Groups */
.input-group {
  position: relative;
  display: flex;
  align-items: center;
}

.input-group input {
  padding-left: var(--spacing-10);
}

.input-group i {
  position: absolute;
  left: var(--spacing-3);
  color: var(--text-tertiary);
  z-index: 1;
}

.password-toggle {
  position: absolute;
  right: var(--spacing-3);
  background: none;
  border: none;
  color: var(--text-tertiary);
  cursor: pointer;
  padding: var(--spacing-2);
  border-radius: var(--border-radius-sm);
  transition: color var(--transition-fast);
}

.password-toggle:hover {
  color: var(--text-secondary);
}

/* Checkbox Styles */
.checkbox-label {
  display: flex;
  align-items: flex-start;
  gap: var(--spacing-2);
  cursor: pointer;
  font-size: var(--font-size-sm);
  line-height: var(--line-height-normal);
}

.checkbox-label input[type="checkbox"] {
  position: absolute;
  opacity: 0;
  cursor: pointer;
}

.checkmark {
  position: relative;
  width: 18px;
  height: 18px;
  min-width: 18px;
  border: 2px solid var(--border-primary);
  border-radius: var(--border-radius-sm);
  background-color: var(--surface);
  transition: all var(--transition-fast);
}

.checkbox-label input:checked + .checkmark {
  background-color: var(--primary-600);
  border-color: var(--primary-600);
}

.checkmark::after {
  content: '';
  position: absolute;
  display: none;
  left: 5px;
  top: 1px;
  width: 6px;
  height: 10px;
  border: solid white;
  border-width: 0 2px 2px 0;
  transform: rotate(45deg);
}

.checkbox-label input:checked + .checkmark::after {
  display: block;
}

/* Toggle Switch */
.toggle-switch {
  position: relative;
  width: 48px;
  height: 24px;
}

.toggle-switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.toggle-slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: var(--neutral-400);
  transition: var(--transition-normal);
  border-radius: var(--border-radius-full);
}

.toggle-slider:before {
  position: absolute;
  content: "";
  height: 18px;
  width: 18px;
  left: 3px;
  bottom: 3px;
  background-color: white;
  transition: var(--transition-normal);
  border-radius: 50%;
}

input:checked + .toggle-slider {
  background-color: var(--primary-600);
}

input:checked + .toggle-slider:before {
  transform: translateX(24px);
}

/* Cards */
.card {
  background-color: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-6);
  box-shadow: var(--shadow-sm);
  transition: all var(--transition-fast);
}

.card:hover {
  box-shadow: var(--shadow-md);
  transform: translateY(-1px);
}

.glass-card {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
  box-shadow: var(--shadow-lg);
}

[data-theme="dark"] .glass-card {
  background: rgba(26, 26, 26, 0.8);
  border-color: rgba(255, 255, 255, 0.1);
}

/* Modals */
.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: var(--z-modal);
  display: none;
  align-items: center;
  justify-content: center;
  padding: var(--spacing-4);
}

.modal.active {
  display: flex;
}

.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: var(--z-modal-backdrop);
  display: none;
}

.modal-overlay.active {
  display: block;
}

.modal-content {
  background-color: var(--surface);
  border-radius: var(--border-radius-xl);
  box-shadow: var(--shadow-2xl);
  width: 100%;
  max-width: 500px;
  max-height: 90vh;
  overflow-y: auto;
  animation: modalSlideIn var(--transition-normal) ease-out;
}

@keyframes modalSlideIn {
  from {
    opacity: 0;
    transform: translateY(-50px) scale(0.9);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

.modal-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--spacing-6);
  border-bottom: 1px solid var(--border-secondary);
}

.modal-header h3 {
  margin: 0;
  font-size: var(--font-size-xl);
}

.modal-close {
  background: none;
  border: none;
  font-size: var(--font-size-xl);
  color: var(--text-tertiary);
  cursor: pointer;
  padding: var(--spacing-2);
  border-radius: var(--border-radius-md);
  transition: all var(--transition-fast);
}

.modal-close:hover {
  color: var(--text-primary);
  background-color: var(--bg-hover);
}

.modal-body {
  padding: var(--spacing-6);
}

/* Toast Notifications */
.toast-container {
  position: fixed;
  bottom: var(--spacing-6);
  right: var(--spacing-6);
  z-index: var(--z-toast);
  max-width: 400px;
}

.toast {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
  padding: var(--spacing-4) var(--spacing-6);
  margin-bottom: var(--spacing-2);
  border-radius: var(--border-radius-lg);
  box-shadow: var(--shadow-lg);
  color: white;
  font-weight: 500;
  animation: toastSlideIn var(--transition-normal) ease-out;
  cursor: pointer;
  transition: transform var(--transition-fast);
}

.toast:hover {
  transform: translateX(-5px);
}

.toast.success {
  background-color: var(--success-600);
}

.toast.error {
  background-color: var(--error-600);
}

.toast.warning {
  background-color: var(--warning-600);
  color: var(--neutral-900);
}

.toast.info {
  background-color: var(--primary-600);
}

@keyframes toastSlideIn {
  from {
    opacity: 0;
    transform: translateX(100%);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

/* Loading Overlay */
.loading-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  display: none;
  align-items: center;
  justify-content: center;
  z-index: var(--z-modal);
}

.loading-overlay.active {
  display: flex;
}

.loading-spinner {
  width: 40px;
  height: 40px;
  border: 4px solid rgba(255, 255, 255, 0.3);
  border-top: 4px solid var(--primary-500);
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Utility Classes */
.container {
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 var(--spacing-4);
}

.text-center { text-align: center; }
.text-left { text-align: left; }
.text-right { text-align: right; }

.font-bold { font-weight: 700; }
.font-semibold { font-weight: 600; }
.font-medium { font-weight: 500; }
.font-normal { font-weight: 400; }

.text-xs { font-size: var(--font-size-xs); }
.text-sm { font-size: var(--font-size-sm); }
.text-base { font-size: var(--font-size-base); }
.text-lg { font-size: var(--font-size-lg); }
.text-xl { font-size: var(--font-size-xl); }
.text-2xl { font-size: var(--font-size-2xl); }

.text-primary { color: var(--text-primary); }
.text-secondary { color: var(--text-secondary); }
.text-tertiary { color: var(--text-tertiary); }
.text-success { color: var(--success-600); }
.text-error { color: var(--error-600); }
.text-warning { color: var(--warning-600); }

.bg-primary { background-color: var(--bg-primary); }
.bg-secondary { background-color: var(--bg-secondary); }
.bg-surface { background-color: var(--surface); }

.border { border: 1px solid var(--border-primary); }
.border-secondary { border: 1px solid var(--border-secondary); }
.rounded { border-radius: var(--border-radius-md); }
.rounded-lg { border-radius: var(--border-radius-lg); }
.rounded-xl { border-radius: var(--border-radius-xl); }
.rounded-2xl { border-radius: var(--border-radius-2xl); }
.rounded-full { border-radius: var(--border-radius-full); }

.shadow { box-shadow: var(--shadow-md); }
.shadow-lg { box-shadow: var(--shadow-lg); }
.shadow-xl { box-shadow: var(--shadow-xl); }

.d-none { display: none; }
.d-block { display: block; }
.d-flex { display: flex; }
.d-inline-flex { display: inline-flex; }
.d-grid { display: grid; }

.flex-row { flex-direction: row; }
.flex-column { flex-direction: column; }
.flex-wrap { flex-wrap: wrap; }
.flex-nowrap { flex-wrap: nowrap; }

.justify-start { justify-content: flex-start; }
.justify-center { justify-content: center; }
.justify-end { justify-content: flex-end; }
.justify-between { justify-content: space-between; }
.justify-around { justify-content: space-around; }

.align-start { align-items: flex-start; }
.align-center { align-items: center; }
.align-end { align-items: flex-end; }
.align-stretch { align-items: stretch; }

.gap-1 { gap: var(--spacing-1); }
.gap-2 { gap: var(--spacing-2); }
.gap-3 { gap: var(--spacing-3); }
.gap-4 { gap: var(--spacing-4); }
.gap-6 { gap: var(--spacing-6); }
.gap-8 { gap: var(--spacing-8); }

.p-1 { padding: var(--spacing-1); }
.p-2 { padding: var(--spacing-2); }
.p-3 { padding: var(--spacing-3); }
.p-4 { padding: var(--spacing-4); }
.p-6 { padding: var(--spacing-6); }
.p-8 { padding: var(--spacing-8); }

.m-1 { margin: var(--spacing-1); }
.m-2 { margin: var(--spacing-2); }
.m-3 { margin: var(--spacing-3); }
.m-4 { margin: var(--spacing-4); }
.m-6 { margin: var(--spacing-6); }
.m-8 { margin: var(--spacing-8); }

.mb-0 { margin-bottom: 0; }
.mb-2 { margin-bottom: var(--spacing-2); }
.mb-4 { margin-bottom: var(--spacing-4); }
.mb-6 { margin-bottom: var(--spacing-6); }
.mb-8 { margin-bottom: var(--spacing-8); }

.mt-0 { margin-top: 0; }
.mt-2 { margin-top: var(--spacing-2); }
.mt-4 { margin-top: var(--spacing-4); }
.mt-6 { margin-top: var(--spacing-6); }
.mt-8 { margin-top: var(--spacing-8); }

/* Responsive Utilities */
@media (max-width: 768px) {
  .container {
    padding: 0 var(--spacing-3);
  }

  .modal-content {
    margin: var(--spacing-4);
    max-height: calc(100vh - 2rem);
  }

  .toast-container {
    left: var(--spacing-4);
    right: var(--spacing-4);
    bottom: var(--spacing-4);
    max-width: none;
  }

  .toast {
    padding: var(--spacing-3) var(--spacing-4);
  }

  .btn-large {
    padding: var(--spacing-3) var(--spacing-6);
    font-size: var(--font-size-base);
  }
}

@media (max-width: 480px) {
  :root {
    --spacing-4: 0.75rem;
    --spacing-6: 1rem;
    --spacing-8: 1.5rem;
  }

  .container {
    padding: 0 var(--spacing-2);
  }
}

/* Authentication Pages */
.auth-body {
  background: linear-gradient(135deg, var(--primary-600) 0%, var(--primary-800) 100%);
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--spacing-4);
}

[data-theme="dark"] .auth-body {
  background: linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #0a0a0a 100%);
}

.auth-container {
  display: grid;
  grid-template-columns: 1fr 1fr;
  max-width: 1000px;
  width: 100%;
  background: var(--surface);
  border-radius: var(--border-radius-2xl);
  box-shadow: var(--shadow-2xl);
  overflow: hidden;
  min-height: 600px;
}

.auth-card {
  padding: var(--spacing-12);
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.auth-visual {
  background: linear-gradient(135deg, var(--primary-600) 0%, var(--primary-800) 100%);
  padding: var(--spacing-12);
  display: flex;
  flex-direction: column;
  justify-content: center;
  color: white;
  position: relative;
  overflow: hidden;
}

.auth-visual::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="grid" width="10" height="10" patternUnits="userSpaceOnUse"><path d="M 10 0 L 0 0 0 10" fill="none" stroke="rgba(255,255,255,0.1)" stroke-width="0.5"/></pattern></defs><rect width="100" height="100" fill="url(%23grid)" /></svg>');
  opacity: 0.3;
}

.auth-header {
  text-align: center;
  margin-bottom: var(--spacing-8);
}

.auth-logo {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--spacing-2);
  font-size: var(--font-size-xl);
  font-weight: 700;
  color: var(--primary-600);
  margin-bottom: var(--spacing-6);
}

.auth-logo i {
  font-size: var(--font-size-2xl);
}

.auth-header h1 {
  font-size: var(--font-size-3xl);
  font-weight: 700;
  margin-bottom: var(--spacing-2);
}

.auth-header p {
  color: var(--text-secondary);
  margin-bottom: 0;
}

.auth-form {
  flex: 1;
}

.form-options {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-6);
}

.forgot-password {
  font-size: var(--font-size-sm);
  color: var(--primary-600);
}

.forgot-password:hover {
  color: var(--primary-700);
}

.form-footer {
  text-align: center;
  margin-top: var(--spacing-6);
  padding-top: var(--spacing-6);
  border-top: 1px solid var(--border-secondary);
  font-size: var(--font-size-sm);
  color: var(--text-secondary);
}

.form-footer a {
  color: var(--primary-600);
  font-weight: 500;
}

.visual-content {
  position: relative;
  z-index: 1;
}

.visual-content h2 {
  font-size: var(--font-size-3xl);
  font-weight: 700;
  margin-bottom: var(--spacing-4);
}

.visual-content p {
  font-size: var(--font-size-lg);
  margin-bottom: var(--spacing-8);
  opacity: 0.9;
}

.visual-features {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-4);
}

.visual-feature {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
  font-weight: 500;
}

.visual-feature i {
  width: 24px;
  text-align: center;
}

.visual-stats {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: var(--spacing-6);
  margin-top: var(--spacing-8);
}

.stat-item {
  text-align: center;
}

.stat-number {
  display: block;
  font-size: var(--font-size-2xl);
  font-weight: 700;
  margin-bottom: var(--spacing-1);
}

.stat-label {
  font-size: var(--font-size-sm);
  opacity: 0.8;
}

.password-strength {
  margin-top: var(--spacing-2);
  display: none;
}

.password-strength.visible {
  display: block;
}

.strength-bar {
  height: 4px;
  background-color: var(--neutral-300);
  border-radius: var(--border-radius-full);
  overflow: hidden;
  margin-bottom: var(--spacing-1);
}

.strength-fill {
  height: 100%;
  transition: width var(--transition-normal), background-color var(--transition-normal);
  width: 0;
}

.strength-bar.weak .strength-fill {
  width: 25%;
  background-color: var(--error-500);
}

.strength-bar.fair .strength-fill {
  width: 50%;
  background-color: var(--warning-500);
}

.strength-bar.good .strength-fill {
  width: 75%;
  background-color: var(--success-500);
}

.strength-bar.strong .strength-fill {
  width: 100%;
  background-color: var(--success-600);
}

.strength-text {
  font-size: var(--font-size-xs);
  font-weight: 500;
}

.strength-bar.weak + .strength-text {
  color: var(--error-600);
}

.strength-bar.fair + .strength-text {
  color: var(--warning-600);
}

.strength-bar.good + .strength-text {
  color: var(--success-600);
}

.strength-bar.strong + .strength-text {
  color: var(--success-700);
}

/* Enhanced Form Styles */
.form-select {
  width: 100%;
  padding: var(--spacing-3) var(--spacing-4);
  border: 1px solid var(--border-primary);
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-base);
  background-color: var(--surface);
  color: var(--text-primary);
  transition: all var(--transition-fast);
  cursor: pointer;
}

.form-select:focus {
  outline: none;
  border-color: var(--border-focus);
  box-shadow: 0 0 0 3px rgba(33, 150, 243, 0.1);
}

/* Settings Page Styles */
.settings-container {
  max-width: 800px;
  margin: 0 auto;
}

.settings-section {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
  margin-bottom: var(--spacing-6);
}

.settings-section.danger {
  border-color: var(--error-300);
}

.settings-section h3 {
  font-size: var(--font-size-xl);
  font-weight: 700;
  margin: 0 0 var(--spacing-6) 0;
  color: var(--text-primary);
  padding-bottom: var(--spacing-3);
  border-bottom: 1px solid var(--border-secondary);
}

.setting-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: var(--spacing-4) 0;
  border-bottom: 1px solid var(--border-secondary);
  transition: background-color var(--transition-fast);
}

.setting-item:last-child {
  border-bottom: none;
}

.setting-item.clickable {
  cursor: pointer;
  margin: 0 calc(-1 * var(--spacing-4));
  padding: var(--spacing-4);
  border-radius: var(--border-radius-lg);
}

.setting-item.clickable:hover {
  background-color: var(--bg-hover);
}

.setting-info {
  flex: 1;
}

.setting-title {
  font-weight: 600;
  color: var(--text-primary);
  margin-bottom: var(--spacing-1);
  display: block;
}

.setting-subtitle {
  font-size: var(--font-size-sm);
  color: var(--text-tertiary);
  display: block;
}

.setting-action {
  background: none;
  border: 1px solid var(--border-primary);
  padding: var(--spacing-2) var(--spacing-3);
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-sm);
  font-weight: 600;
  color: var(--text-secondary);
  cursor: pointer;
  transition: all var(--transition-fast);
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
}

.setting-action:hover {
  background: var(--primary-500);
  border-color: var(--primary-500);
  color: white;
}

.setting-action.danger {
  border-color: var(--error-500);
  color: var(--error-600);
}

.setting-action.danger:hover {
  background: var(--error-500);
  color: white;
}

.setting-select {
  padding: var(--spacing-2) var(--spacing-3);
  border: 1px solid var(--border-primary);
  border-radius: var(--border-radius-md);
  background: var(--surface);
  color: var(--text-primary);
  font-size: var(--font-size-sm);
  cursor: pointer;
}

.status-text {
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

/* Empty State */
.c-empty-state {
  text-align: center;
  padding: var(--spacing-12);
  color: var(--text-tertiary);
}

.empty-icon {
  font-size: var(--font-size-4xl);
  margin-bottom: var(--spacing-4);
  opacity: 0.5;
}

.c-empty-state h3 {
  font-size: var(--font-size-xl);
  font-weight: 600;
  margin-bottom: var(--spacing-2);
  color: var(--text-secondary);
}

.c-empty-state p {
  font-size: var(--font-size-base);
  margin: 0;
}

/* Enhanced Notification Styles */
.c-notification-item {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
  margin-bottom: var(--spacing-3);
  transition: all var(--transition-fast);
  cursor: pointer;
  position: relative;
  display: flex;
  align-items: flex-start;
  gap: var(--spacing-4);
}

.c-notification-item:hover {
  box-shadow: var(--shadow-md);
  border-color: var(--primary-300);
  transform: translateY(-1px);
}

.c-notification-item.unread {
  border-left: 4px solid var(--primary-500);
  background: linear-gradient(135deg, var(--primary-50) 0%, var(--surface) 100%);
}

[data-theme="dark"] .c-notification-item.unread {
  background: linear-gradient(135deg, rgba(33, 150, 243, 0.05) 0%, var(--surface) 100%);
}

.notification-icon {
  width: 40px;
  height: 40px;
  border-radius: var(--border-radius-full);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: var(--font-size-base);
  color: white;
  flex-shrink: 0;
}

.notification-icon.welcome {
  background: linear-gradient(135deg, var(--primary-500) 0%, var(--primary-700) 100%);
}

.notification-icon.trade {
  background: linear-gradient(135deg, var(--success-500) 0%, var(--success-700) 100%);
}

.notification-icon.payment {
  background: linear-gradient(135deg, var(--warning-500) 0%, var(--warning-700) 100%);
}

.notification-icon.withdrawal {
  background: linear-gradient(135deg, var(--error-500) 0%, var(--error-700) 100%);
}

.notification-icon.system {
  background: linear-gradient(135deg, var(--neutral-500) 0%, var(--neutral-700) 100%);
}

.notification-icon.default {
  background: linear-gradient(135deg, var(--neutral-500) 0%, var(--neutral-700) 100%);
}

.notification-content {
  flex: 1;
}

.notification-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: var(--spacing-2);
}

.notification-title {
  font-weight: 700;
  color: var(--text-primary);
  margin: 0;
  font-size: var(--font-size-base);
}

.notification-time {
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
  flex-shrink: 0;
  margin-left: var(--spacing-2);
}

.notification-body {
  color: var(--text-secondary);
  line-height: var(--line-height-relaxed);
  margin: 0;
  font-size: var(--font-size-sm);
}

.unread-indicator {
  position: absolute;
  top: var(--spacing-3);
  right: var(--spacing-3);
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: var(--primary-500);
}

/* Transaction Row Enhancements */
.u-transaction-row {
  transition: all var(--transition-fast);
}

.u-transaction-row:hover {
  background-color: var(--bg-hover);
  transform: translateX(2px);
}

.c-transaction-date {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

.c-transaction-date .date {
  font-weight: 600;
  color: var(--text-primary);
}

.c-transaction-date .time {
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
}

.c-transaction-type {
  display: flex;
  align-items: center;
  gap: var(--spacing-3);
}

.type-icon {
  width: 32px;
  height: 32px;
  border-radius: var(--border-radius-full);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: var(--font-size-sm);
  color: white;
}

.type-icon.deposit {
  background: var(--success-500);
}

.type-icon.withdraw {
  background: var(--error-500);
}

.type-icon.trade {
  background: var(--primary-500);
}

.type-icon.fee {
  background: var(--warning-500);
}

.type-icon.interest {
  background: var(--success-600);
}

.type-icon.bonus {
  background: var(--secondary-500);
}

.type-text {
  font-weight: 600;
  color: var(--text-primary);
  text-transform: capitalize;
}

.c-transaction-amount {
  font-weight: 800;
  font-feature-settings: 'tnum';
  font-size: var(--font-size-base);
}

.c-transaction-amount.positive {
  color: var(--success-600);
}

.c-transaction-amount.negative {
  color: var(--error-600);
}

.c-transaction-currency {
  font-weight: 600;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.1em;
}

.c-transaction-note {
  color: var(--text-tertiary);
  font-size: var(--font-size-sm);
  max-width: 200px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

/* Mobile Responsive */
@media (max-width: 768px) {
  .auth-container {
    grid-template-columns: 1fr;
    max-width: 400px;
    min-height: auto;
  }

  .auth-visual {
    order: -1;
    padding: var(--spacing-8);
    min-height: 200px;
  }

  .auth-card {
    padding: var(--spacing-8);
  }

  .visual-content h2 {
    font-size: var(--font-size-2xl);
  }

  .visual-content p {
    font-size: var(--font-size-base);
    margin-bottom: var(--spacing-6);
  }

  .visual-stats {
    grid-template-columns: repeat(3, 1fr);
    gap: var(--spacing-4);
    margin-top: var(--spacing-6);
  }

  .stat-number {
    font-size: var(--font-size-lg);
  }
}

@media (max-width: 480px) {
  .auth-body {
    padding: var(--spacing-2);
  }

  .auth-card,
  .auth-visual {
    padding: var(--spacing-6);
  }

  .auth-header h1 {
    font-size: var(--font-size-2xl);
  }

  .visual-content h2 {
    font-size: var(--font-size-xl);
  }

  .form-options {
    flex-direction: column;
    gap: var(--spacing-3);
    align-items: flex-start;
  }
}

@media (max-width: 480px) {
  .settings-section {
    padding: var(--spacing-4);
  }

  .setting-item {
    flex-direction: column;
    align-items: flex-start;
    gap: var(--spacing-3);
  }

  .setting-action {
    align-self: flex-end;
  }

  .c-notification-item {
    flex-direction: column;
    text-align: center;
  }

  .notification-header {
    flex-direction: column;
    text-align: center;
    gap: var(--spacing-1);
  }
}

