  // Setup user balance synchronization
  async setupUserBalanceSync() {
    const balanceSubscription = supabase
      .channel(`user-balance-${this.currentUser.id}`)
      .on(
        "postgres_changes",
        {
          event: "UPDATE",
          schema: "public",
