// Supabase compatibility shim for Firebase
// This provides a minimal Supabase-like API that translates to Firebase

(function () {
  "use strict";

  // Wait for Firebase to be ready
  function waitForFirebase() {
    return new Promise((resolve) => {
      const checkFirebase = () => {
        if (
          window.FIREBASE_INITIALIZED &&
          window.FIREBASE_DB &&
          window.FIREBASE_AUTH
        ) {
          resolve({
            db: window.FIREBASE_DB,
            auth: window.FIREBASE_AUTH,
            firebase: window.firebase,
          });
        } else {
          setTimeout(checkFirebase, 100);
        }
      };
      checkFirebase();
    });
  }

  // Create a minimal Supabase-compatible client
  const createSupabaseShim = (fb) => {
    return {
      from: (table) => ({
        select: async (columns = "*") => {
          try {
            if (!fb.db) {
              return { data: [], error: new Error("Firestore not available") };
            }
            const snapshot = await fb.db.collection(table).get();
            const data = snapshot.docs.map((doc) => ({
              id: doc.id,
              ...doc.data(),
            }));
            return { data, error: null };
          } catch (error) {
            console.error("Supabase shim select error:", error);
            return { data: null, error };
          }
        },
        insert: async (data) => {
          try {
            if (!fb.db) {
              return {
                data: null,
                error: new Error("Firestore not available"),
              };
            }
            const docRef = await fb.db.collection(table).add(data);
            const doc = await docRef.get();
            return {
              data: { id: doc.id, ...doc.data() },
              error: null,
            };
          } catch (error) {
            console.error("Supabase shim insert error:", error);
            return { data: null, error };
          }
        },
        update: async (data) => {
          try {
            if (!fb.db) {
              return {
                data: null,
                error: new Error("Firestore not available"),
              };
            }
            // Update requires an ID
            if (!data.id) {
              return { data: null, error: new Error("ID required for update") };
            }
            await fb.db.collection(table).doc(data.id).update(data);
            const doc = await fb.db.collection(table).doc(data.id).get();
            return {
              data: { id: doc.id, ...doc.data() },
              error: null,
            };
          } catch (error) {
            console.error("Supabase shim update error:", error);
            return { data: null, error };
          }
        },
        delete: async () => {
          // Would need to be chained with eq() or similar
          return {
            data: null,
            error: new Error("Delete not implemented in shim"),
          };
        },
        eq: function (column, value) {
          // Chain methods for query building
          return this;
        },
        single: function () {
          return this;
        },
      }),
      auth: {
        signUp: async ({ email, password }) => {
          try {
            const userCred = await fb.auth.createUserWithEmailAndPassword(
              email,
              password
            );
            return {
              data: { user: userCred.user },
              error: null,
            };
          } catch (error) {
            return { data: null, error };
          }
        },
        signIn: async ({ email, password }) => {
          try {
            const userCred = await fb.auth.signInWithEmailAndPassword(
              email,
              password
            );
            return {
              data: { user: userCred.user },
              error: null,
            };
          } catch (error) {
            return { data: null, error };
          }
        },
        signOut: async () => {
          try {
            await fb.auth.signOut();
            return { error: null };
          } catch (error) {
            return { error };
          }
        },
        getUser: async () => {
          const user = fb.auth.currentUser;
          return {
            data: { user },
            error: null,
          };
        },
      },
    };
  };

  // Initialize shim when Firebase is ready
  waitForFirebase()
    .then((fb) => {
      window.supabase = createSupabaseShim(fb);
      console.log("✅ Supabase shim initialized");
    })
    .catch((err) => {
      console.error("❌ Supabase shim initialization failed:", err);
      // Provide empty shim
      window.supabase = {
        from: () => ({
          select: () => Promise.resolve({ data: [], error: null }),
        }),
        auth: {},
      };
    });
})();

