public/css/landing.css.content:
/* Landing Page Styles */

/* Navigation */




.navbar {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: var(--z-sticky);
  background: rgba(26, 26, 26, 0.95);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  transition: all var(--transition-normal);
}

[data-theme="light"] .navbar {
  background: rgba(255, 255, 255, 0.95);
  border-bottom-color: var(--border-secondary);
}

.navbar.scrolled {
  background: var(--surface-elevated);
  box-shadow: var(--shadow-md);
}

.nav-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 var(--spacing-4);
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 80px;
}

.nav-logo {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  font-size: var(--font-size-xl);
  font-weight: 700;
  color: var(--text-primary);
}

.nav-logo i {
  font-size: var(--font-size-2xl);
  color: var(--primary-500);
}

.nav-menu {
  display: flex;
  list-style: none;
  gap: var(--spacing-8);
  margin: 0;
  padding: 0;
}

.nav-link {
  color: var(--text-secondary);
  font-weight: 500;
  padding: var(--spacing-2) 0;
  position: relative;
  transition: color var(--transition-fast);
}

.nav-link:hover,
.nav-link.active {
  color: var(--primary-500);
}

.nav-link::after {
  content: '';
  position: absolute;
  bottom: -2px;
  left: 0;
  width: 0;
  height: 2px;
  background-color: var(--primary-500);
  transition: width var(--transition-fast);
}

.nav-link:hover::after,
.nav-link.active::after {
  width: 100%;
}

.nav-actions {
  display: flex;
  align-items: center;
  gap: var(--spacing-4);
}

.theme-toggle {
  background: none;
  border: none;
  color: var(--text-tertiary);
  font-size: var(--font-size-lg);
  cursor: pointer;
  padding: var(--spacing-2);
  border-radius: var(--border-radius-full);
  transition: all var(--transition-fast);
}

.theme-toggle:hover {
  color: var(--primary-500);
  background-color: var(--bg-hover);
}

.hamburger {
  display: none;
  flex-direction: column;
  gap: 4px;
  cursor: pointer;
  padding: var(--spacing-2);
}

.hamburger span {
  width: 24px;
  height: 2px;
  background-color: var(--text-primary);
  transition: all var(--transition-fast);
}

.hamburger.active span:nth-child(1) {
  transform: rotate(45deg) translate(6px, 6px);
}

.hamburger.active span:nth-child(2) {
  opacity: 0;
}

.hamburger.active span:nth-child(3) {
  transform: rotate(-45deg) translate(6px, -6px);
}

/* Enhanced 3D Hero Section */
.hero {
  padding: calc(80px + var(--spacing-20)) 0 var(--spacing-20);
  background: linear-gradient(135deg, var(--bg-primary) 0%, var(--bg-tertiary) 100%);
  position: relative;
  overflow: hidden;
  perspective: 1000px;
  min-height: 100vh;
  display: flex;
  align-items: center;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(ellipse at top, rgba(33, 150, 243, 0.1) 0%, transparent 50%);
  pointer-events: none;
}

/* 3D Parallax Layers */
.hero::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background:
    radial-gradient(circle at 20% 80%, rgba(120, 119, 198, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 40% 40%, rgba(120, 219, 255, 0.05) 0%, transparent 50%);
  animation: parallax-bg 20s ease-in-out infinite alternate;
}

@keyframes parallax-bg {
  0% { transform: translateY(0px) rotate(0deg); }
  100% { transform: translateY(-20px) rotate(1deg); }
}

/* 3D Floating Elements */
.hero .floating-element-1 {
  position: absolute;
  top: 10%;
  right: 10%;
  width: 100px;
  height: 100px;
  background: linear-gradient(45deg, var(--primary-500), var(--primary-700));
  border-radius: 50%;
  opacity: 0.1;
  animation: float-3d-1 8s ease-in-out infinite;
  transform-style: preserve-3d;
}

.hero .floating-element-2 {
  position: absolute;
  bottom: 20%;
  left: 5%;
  width: 60px;
  height: 60px;
  background: linear-gradient(45deg, var(--success-500), var(--success-700));
  border-radius: 50%;
  opacity: 0.08;
  animation: float-3d-2 6s ease-in-out infinite reverse;
  transform-style: preserve-3d;
}

@keyframes float-3d-1 {
  0%, 100% {
    transform: translateY(0px) translateX(0px) rotateY(0deg) rotateX(0deg);
  }
  25% {
    transform: translateY(-15px) translateX(10px) rotateY(5deg) rotateX(2deg);
  }
  50% {
    transform: translateY(-10px) translateX(-5px) rotateY(-3deg) rotateX(-1deg);
  }
  75% {
    transform: translateY(-20px) translateX(15px) rotateY(8deg) rotateX(3deg);
  }
}

@keyframes float-3d-2 {
  0%, 100% {
    transform: translateY(0px) translateX(0px) rotateY(0deg) rotateX(0deg);
  }
  33% {
    transform: translateY(-12px) translateX(-8px) rotateY(-4deg) rotateX(-2deg);
  }
  66% {
    transform: translateY(-18px) translateX(12px) rotateY(6deg) rotateX(3deg);
  }
}

.hero-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 var(--spacing-4);
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: var(--spacing-12);
  align-items: center;
}

.hero-content {
  position: relative;
  z-index: 1;
  transform-style: preserve-3d;
}

.hero-title {
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 800;
  line-height: 1.1;
  margin-bottom: var(--spacing-6);
  color: var(--text-primary);
  transform: translateZ(0);
  animation: title-3d 1s ease-out;
}

@keyframes title-3d {
  0% {
    opacity: 0;
    transform: translateZ(-50px) rotateX(-10deg);
  }
  100% {
    opacity: 1;
    transform: translateZ(0) rotateX(0deg);
  }
}

.gradient-text {
  background: linear-gradient(135deg, var(--primary-500) 0%, var(--primary-700) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  display: inline-block;
  animation: gradient-shift 3s ease-in-out infinite alternate;
}

@keyframes gradient-shift {
  0% { background-position: 0% 50%; }
  100% { background-position: 100% 50%; }
}

.hero-subtitle {
  font-size: var(--font-size-xl);
  color: var(--text-secondary);
  margin-bottom: var(--spacing-8);
  line-height: var(--line-height-relaxed);
  transform: translateZ(0);
  animation: subtitle-3d 1s ease-out 0.2s both;
}

@keyframes subtitle-3d {
  0% {
    opacity: 0;
    transform: translateZ(-30px) translateY(20px);
  }
  100% {
    opacity: 1;
    transform: translateZ(0) translateY(0);
  }
}

.hero-actions {
  display: flex;
  gap: var(--spacing-4);
  margin-bottom: var(--spacing-12);
  transform: translateZ(0);
  animation: actions-3d 1s ease-out 0.4s both;
}

@keyframes actions-3d {
  0% {
    opacity: 0;
    transform: translateZ(-20px) translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateZ(0) translateY(0);
  }
}

.hero-stats {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: var(--spacing-6);
  transform: translateZ(0);
  animation: stats-3d 1s ease-out 0.6s both;
}

@keyframes stats-3d {
  0% {
    opacity: 0;
    transform: translateZ(-10px) translateY(20px);
  }
  100% {
    opacity: 1;
    transform: translateZ(0) translateY(0);
  }
}

.stat-item {
  text-align: center;
  padding: var(--spacing-4);
  background: rgba(255, 255, 255, 0.05);
  border-radius: var(--border-radius-lg);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all var(--transition-normal);
  transform-style: preserve-3d;
}

.stat-item:hover {
  transform: translateY(-5px) rotateX(5deg);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
  background: rgba(255, 255, 255, 0.08);
}

.stat-number {
  display: block;
  font-size: var(--font-size-2xl);
  font-weight: 700;
  color: var(--primary-600);
  margin-bottom: var(--spacing-1);
  animation: counter-3d 2s ease-out 1s both;
}

@keyframes counter-3d {
  0% {
    opacity: 0;
    transform: scale(0.5) translateZ(-20px);
  }
  100% {
    opacity: 1;
    transform: scale(1) translateZ(0);
  }
}

.stat-label {
  font-size: var(--font-size-sm);
  color: var(--text-tertiary);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.hero-visual {
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  transform-style: preserve-3d;
  animation: visual-3d 1s ease-out 0.8s both;
}

@keyframes visual-3d {
  0% {
    opacity: 0;
    transform: translateZ(-100px) rotateY(-10deg);
  }
  100% {
    opacity: 1;
    transform: translateZ(0) rotateY(0deg);
  }
}

.trading-card {
  background: var(--surface-elevated);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-2xl);
  padding: var(--spacing-6);
  box-shadow: var(--shadow-xl);
  width: 100%;
  max-width: 400px;
  position: relative;
  overflow: hidden;
  transform-style: preserve-3d;
  transition: all var(--transition-normal);
}

.live-chart-card {
  background: var(--surface-elevated);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-2xl);
  padding: var(--spacing-6);
  box-shadow: var(--shadow-xl);
  width: 100%;
  max-width: 500px;
  position: relative;
  overflow: hidden;
  transform-style: preserve-3d;
  transition: all var(--transition-normal);
  animation: card-float 6s ease-in-out infinite;
}

.live-chart-card:hover {
  transform: translateY(-10px) rotateX(5deg) rotateY(5deg);
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
}

@keyframes card-float {
  0%, 100% {
    transform: translateY(0px) rotateX(0deg) rotateY(0deg);
  }
  50% {
