.footer {
  background: var(--bg-tertiary);
  padding: var(--spacing-16) 0 var(--spacing-8);
  border-top: 1px solid var(--border-secondary);
}

.footer-content {
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1fr;
  gap: var(--spacing-12);
  margin-bottom: var(--spacing-8);
}

.footer-section h4 {
  font-size: var(--font-size-lg);
  font-weight: 600;
  margin-bottom: var(--spacing-6);
  color: var(--text-primary);
}

.footer-logo {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  font-size: var(--font-size-xl);
  font-weight: 700;
  color: var(--text-primary);
  margin-bottom: var(--spacing-4);
}

.footer-logo i {
  font-size: var(--font-size-2xl);
  color: var(--primary-500);
}

.footer-section p {
  color: var(--text-secondary);
  line-height: var(--line-height-relaxed);
  margin-bottom: var(--spacing-6);
}

.social-links {
  display: flex;
  gap: var(--spacing-3);
}

.social-links a {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: var(--border-radius-full);
  background: var(--bg-hover);
  color: var(--text-tertiary);
  transition: all var(--transition-fast);
}

.social-links a:hover {
  background: var(--primary-500);
  color: white;
  transform: translateY(-2px);
}

.footer-section ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.footer-section ul li {
  margin-bottom: var(--spacing-3);
}

.footer-section ul a {
  color: var(--text-secondary);
  transition: color var(--transition-fast);
}

.footer-section ul a:hover {
  color: var(--primary-500);
}

.footer-bottom {
  padding-top: var(--spacing-8);
  border-top: 1px solid var(--border-secondary);
  text-align: center;
  color: var(--text-tertiary);
  font-size: var(--font-size-sm);
}

.footer-bottom p {
  margin-bottom: var(--spacing-2);
}

/* Mobile Navigation */
@media (max-width: 768px) {
  .hamburger {
    display: flex;
  }

  .nav-menu {
    position: fixed;
    top: 80px;
    left: 0;
    width: 100%;
    height: calc(100vh - 80px);
    background: var(--surface-elevated);
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    gap: var(--spacing-4);
    padding: var(--spacing-8);
    transform: translateX(-100%);
    transition: transform var(--transition-normal);
  }

  .nav-menu.active {
    transform: translateX(0);
  }

  .nav-actions .btn {
    display: none;
  }

  .nav-actions .theme-toggle {
    display: flex;
  }

  /* Hero Mobile */
  .hero-container {
    grid-template-columns: 1fr;
    gap: var(--spacing-8);
    text-align: center;
  }

  .hero-visual {
    order: -1;
  }

  .live-chart-card {
    max-width: 100%;
    margin: 0 auto;
  }

  .live-chart-container {
    height: 180px;
  }

  .hero-stats {
    grid-template-columns: repeat(3, 1fr);
    gap: var(--spacing-4);
  }

  .stat-number {
    font-size: var(--font-size-xl);
  }

  /* About Mobile */
  .about-grid {
    grid-template-columns: 1fr;
    gap: var(--spacing-8);
    text-align: center;
  }

  .about-features {
    grid-template-columns: 1fr;
  }

  /* Features Mobile */
  .features-grid {
    grid-template-columns: 1fr;
    gap: var(--spacing-6);
  }

  /* Testimonials Mobile */
  .testimonials-grid {
    grid-template-columns: 1fr;
    gap: var(--spacing-6);
  }

  /* CTA Mobile */
  .cta-actions {
    flex-direction: column;
    align-items: center;
  }

  /* Footer Mobile */
  .footer-content {
    grid-template-columns: 1fr;
    gap: var(--spacing-8);
    text-align: center;
  }
}

@media (max-width: 480px) {
  .nav-container {
    padding: 0 var(--spacing-3);
    height: 70px;
  }

  .hero {
    padding: calc(70px + var(--spacing-12)) 0 var(--spacing-12);
  }

  .hero-title {
    font-size: 2rem;
  }

  .hero-subtitle {
    font-size: var(--font-size-base);
  }

  .hero-actions {
    flex-direction: column;
    gap: var(--spacing-3);
  }

  .section-header h2 {
    font-size: var(--font-size-2xl);
  }

  .feature-card,
  .testimonial-card {
    padding: var(--spacing-6);
  }

  .trading-card {
    padding: var(--spacing-4);
  }

  .live-chart-card {
    padding: var(--spacing-4);
  }

  .chart-header {
    flex-direction: column;
    gap: var(--spacing-3);
    text-align: center;
  }

  .price-info {
    text-align: center;
  }

  .current-price {
    font-size: var(--font-size-xl);
  }

  .live-chart-container {
    height: 180px;
  }

  .chart-stats {
    grid-template-columns: repeat(3, 1fr);
    gap: var(--spacing-2);
  }

  .price {
    font-size: var(--font-size-xl);
  }

  .cta-content h2 {
    font-size: var(--font-size-2xl);
  }

  .chart-stats .value {
    font-size: var(--font-size-sm);
  }

  .hero-container {
    gap: var(--spacing-6);
  }

  .hero-stats {
    grid-template-columns: repeat(3, 1fr);
    gap: var(--spacing-3);
  }

  .stat-number {
    font-size: var(--font-size-lg);
  }
}

public/css/trading.css.content:
/* Trading-specific styles */

/* Enhanced Trading Grid */

.trading-grid {
  display: grid;
  grid-template-columns: 300px 1fr 320px;
  gap: var(--spacing-6);
  margin-bottom: var(--spacing-8);
  min-height: 600px;
}

/* Price Panel Enhancements */
.price-panel {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
  display: flex;
  flex-direction: column;
  gap: var(--spacing-4);
}

.current-price {
  background: linear-gradient(135deg, var(--bg-tertiary) 0%, var(--bg-hover) 100%);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
  text-align: center;
}

.current-price .symbol {
  font-size: var(--font-size-sm);
  font-weight: 600;
  color: var(--text-tertiary);
  text-transform: uppercase;
  letter-spacing: 0.1em;
  display: block;
  margin-bottom: var(--spacing-1);
}

.current-price .price {
  font-size: var(--font-size-3xl);
  font-weight: 800;
  color: var(--text-primary);
  font-feature-settings: 'tnum';
  display: block;
  margin-bottom: var(--spacing-2);
}

.current-price .change {
  font-size: var(--font-size-base);
  font-weight: 600;
  padding: var(--spacing-1) var(--spacing-3);
  border-radius: var(--border-radius-full);
  display: inline-block;
}

/* Market Stats */
.price-stats {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-3);
}

.stat {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: var(--spacing-2) 0;
  border-bottom: 1px solid var(--border-secondary);
}

.stat:last-child {
  border-bottom: none;
}

.stat .label {
  font-size: var(--font-size-sm);
  color: var(--text-tertiary);
  font-weight: 500;
}

.stat .value {
  font-size: var(--font-size-sm);
  font-weight: 700;
  color: var(--text-primary);
  font-feature-settings: 'tnum';
}

/* Chart Panel Enhancements */
.chart-panel {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
  display: flex;
  flex-direction: column;
}

.chart-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-4);
  padding-bottom: var(--spacing-3);
  border-bottom: 1px solid var(--border-secondary);
}

.timeframe-selector {
  display: flex;
  gap: var(--spacing-1);
  background: var(--bg-tertiary);
  padding: var(--spacing-1);
  border-radius: var(--border-radius-lg);
}

.timeframe-btn {
  background: none;
  border: none;
  padding: var(--spacing-2) var(--spacing-4);
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-sm);
  font-weight: 600;
  color: var(--text-secondary);
  cursor: pointer;
  transition: all var(--transition-fast);
  min-width: 48px;
}

.timeframe-btn:hover {
  background-color: var(--bg-hover);
  color: var(--text-primary);
}

.timeframe-btn.active {
  background-color: var(--primary-500);
  color: white;
  box-shadow: var(--shadow-sm);
}

.chart-area {
  flex: 1;
  min-height: 400px;
  background: var(--bg-tertiary);
  border-radius: var(--border-radius-lg);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
}

/* Trading Panel Enhancements */
.trading-panel {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
  display: flex;
  flex-direction: column;
}

.trade-tabs {
  display: flex;
  background: var(--bg-tertiary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-1);
  margin-bottom: var(--spacing-6);
}

.trade-tab {
  flex: 1;
  background: none;
  border: none;
  padding: var(--spacing-3);
  border-radius: var(--border-radius-md);
  font-weight: 700;
  font-size: var(--font-size-base);
  cursor: pointer;
  transition: all var(--transition-fast);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.trade-tab.active {
  background-color: var(--surface);
  color: var(--primary-600);
  box-shadow: var(--shadow-sm);
}

.trade-form {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-4);
}

.trade-form .form-group {
  position: relative;
}

.trade-form label {
  display: block;
  margin-bottom: var(--spacing-2);
  font-size: var(--font-size-sm);
  font-weight: 600;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.input-suffix {
  position: absolute;
  right: var(--spacing-4);
  top: 50%;
  transform: translateY(-50%);
  font-size: var(--font-size-sm);
  color: var(--text-tertiary);
  font-weight: 600;
  pointer-events: none;
  background: var(--surface);
  padding: 0 var(--spacing-1);
}

/* Advanced Options */
.advanced-options {
  border-top: 1px solid var(--border-secondary);
  padding-top: var(--spacing-4);
  margin-top: var(--spacing-4);
}

.advanced-options .form-group {
  margin-bottom: var(--spacing-3);
}

/* Trade Summary */
.trade-summary {
  background: linear-gradient(135deg, var(--bg-tertiary) 0%, var(--bg-hover) 100%);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
  margin: var(--spacing-4) 0;
  border: 1px solid var(--border-secondary);
}

.summary-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-2);
  font-size: var(--font-size-sm);
}

.summary-item:last-child {
  margin-bottom: 0;
}

.summary-item.total {
  font-weight: 700;
  font-size: var(--font-size-base);
  padding-top: var(--spacing-3);
  border-top: 1px solid var(--border-primary);
  margin-top: var(--spacing-3);
}

/* Execute Button */
.execute-btn {
  width: 100%;
  padding: var(--spacing-4) var(--spacing-6);
  font-size: var(--font-size-lg);
  font-weight: 700;
  border-radius: var(--border-radius-xl);
  border: none;
  cursor: pointer;
  transition: all var(--transition-fast);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  position: relative;
  overflow: hidden;
}

.execute-btn::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s;
}

.execute-btn:hover::before {
  left: 100%;
}

.buy-btn {
  background: linear-gradient(135deg, var(--success-500) 0%, var(--success-700) 100%);
  color: white;
  box-shadow: 0 4px 15px rgba(76, 175, 80, 0.3);
}

.buy-btn:hover {
  background: linear-gradient(135deg, var(--success-600) 0%, var(--success-800) 100%);
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(76, 175, 80, 0.4);
}

.sell-btn {
  background: linear-gradient(135deg, var(--error-500) 0%, var(--error-700) 100%);
  color: white;
  box-shadow: 0 4px 15px rgba(244, 67, 54, 0.3);
}

.sell-btn:hover {
  background: linear-gradient(135deg, var(--error-600) 0%, var(--error-800) 100%);
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(244, 67, 54, 0.4);
}

/* Open Trades Enhancements */
.open-trades {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
}

.trades-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-6);
  padding-bottom: var(--spacing-3);
  border-bottom: 1px solid var(--border-secondary);
}

.trades-header h3 {
  font-size: var(--font-size-xl);
  font-weight: 700;
  color: var(--text-primary);
  margin: 0;
}

.trade-count {
  font-size: var(--font-size-sm);
  color: var(--text-tertiary);
  background: var(--bg-tertiary);
  padding: var(--spacing-1) var(--spacing-3);
  border-radius: var(--border-radius-full);
  font-weight: 600;
}

/* Enhanced Table Styles */
.trades-table {
  border-radius: var(--border-radius-lg);
  overflow: hidden;
  border: 1px solid var(--border-secondary);
}

.trades-table table {
  width: 100%;
  border-collapse: collapse;
}

.trades-table thead {
  background: linear-gradient(135deg, var(--bg-tertiary) 0%, var(--bg-hover) 100%);
}

.trades-table th {
  padding: var(--spacing-4);
  text-align: left;
  font-size: var(--font-size-xs);
  font-weight: 700;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.1em;
  border-bottom: 1px solid var(--border-secondary);
  white-space: nowrap;
}

.trades-table td {
  padding: var(--spacing-4);
  border-bottom: 1px solid var(--border-secondary);
  font-size: var(--font-size-sm);
  vertical-align: middle;
}

.trades-table tbody tr {
  transition: background-color var(--transition-fast);
}

.trades-table tbody tr:hover {
  background-color: var(--bg-hover);
}

.trades-table tbody tr:last-child td {
  border-bottom: none;
}

/* P&L Styling */
.pnl {
  font-weight: 800;
  font-feature-settings: 'tnum';
  font-size: var(--font-size-base);
}

.pnl.positive {
  color: var(--success-600);
}

.pnl.negative {
  color: var(--error-600);
}

.pnl::before {
  content: '';
  display: inline-block;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  margin-right: var(--spacing-2);
}

.pnl.positive::before {
  background-color: var(--success-500);
}

.pnl.negative::before {
  background-color: var(--error-500);
}

/* Action Buttons */
.action-buttons {
  display: flex;
  gap: var(--spacing-1);
}

.action-btn {
  background: none;
  border: 1px solid var(--border-primary);
  padding: var(--spacing-1) var(--spacing-3);
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-xs);
  font-weight: 600;
  cursor: pointer;
  transition: all var(--transition-fast);
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.action-btn:hover {
  background-color: var(--primary-500);
  border-color: var(--primary-500);
  color: white;
  transform: translateY(-1px);
  box-shadow: var(--shadow-sm);
}

.action-btn.danger:hover {
  background-color: var(--error-500);
  border-color: var(--error-500);
}

/* Mobile Trading Optimizations */
@media (max-width: 1200px) {
  .trading-grid {
    grid-template-columns: 1fr;
    gap: var(--spacing-4);
  }

  .price-panel {
    order: 1;
  }

  .chart-panel {
    order: 2;
  }

  .trading-panel {
    order: 3;
  }
}

@media (max-width: 768px) {
  .current-price .price {
    font-size: var(--font-size-2xl);
  }

  .timeframe-selector {
    flex-wrap: wrap;
    gap: var(--spacing-1);
  }

  .timeframe-btn {
    flex: 1;
    min-width: 60px;
  }

  .chart-area {
    min-height: 300px;
  }

  .trade-tabs {
    margin-bottom: var(--spacing-4);
  }

  .execute-btn {
    padding: var(--spacing-4);
    font-size: var(--font-size-base);
  }

  .trades-table {
    font-size: var(--font-size-xs);
  }

  .trades-table th,
  .trades-table td {
    padding: var(--spacing-2);
  }
}

@media (max-width: 480px) {
  .price-stats {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: var(--spacing-2);
  }

  .stat {
    flex-direction: column;
    text-align: center;
    border: 1px solid var(--border-secondary);
    border-radius: var(--border-radius-md);
    padding: var(--spacing-3);
    border-bottom: 1px solid var(--border-secondary);
  }

  .stat .label {
    margin-bottom: var(--spacing-1);
  }

  .advanced-options {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-3);
  }

  .summary-item {
    font-size: var(--font-size-xs);
  }
}

/* Deposit/Withdraw Page Styles */
.deposit-container,
.withdraw-container {
  max-width: 800px;
  margin: 0 auto;
}

.balance-overview {
  margin-bottom: var(--spacing-8);
}

.balance-breakdown {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: var(--spacing-4);
  margin-top: var(--spacing-4);
  padding-top: var(--spacing-4);
  border-top: 1px solid var(--border-secondary);
}

.breakdown-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: var(--font-size-sm);
}

.breakdown-item span:first-child {
  color: var(--text-tertiary);
  font-weight: 500;
}

.breakdown-item span:last-child {
  color: var(--text-primary);
  font-weight: 700;
  font-feature-settings: 'tnum';
}

/* Method Selection */
.deposit-methods,
.withdraw-methods {
  margin-bottom: var(--spacing-8);
}

.deposit-methods h3,
.withdraw-methods h3 {
  font-size: var(--font-size-xl);
  font-weight: 700;
  margin-bottom: var(--spacing-6);
  color: var(--text-primary);
}

.methods-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: var(--spacing-4);
}

.methods-list {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-3);
}

.method-card,
.method-item {
  background: var(--surface);
  border: 2px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-6);
  cursor: pointer;
  transition: all var(--transition-fast);
  position: relative;
  overflow: hidden;
}

.method-card:hover,
.method-item:hover {
  border-color: var(--primary-300);
  transform: translateY(-2px);
  box-shadow: var(--shadow-lg);
}

.method-card.active,
.method-item.active {
  border-color: var(--primary-500);
  background: linear-gradient(135deg, var(--primary-50) 0%, var(--surface) 100%);
}

[data-theme="dark"] .method-card.active,
[data-theme="dark"] .method-item.active {
  background: linear-gradient(135deg, rgba(33, 150, 243, 0.1) 0%, var(--surface) 100%);
}

.method-icon {
  width: 48px;
  height: 48px;
  border-radius: var(--border-radius-xl);
  background: linear-gradient(135deg, var(--primary-500) 0%, var(--primary-700) 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: var(--font-size-xl);
  color: white;
  margin-bottom: var(--spacing-4);
}

.method-info h4 {
  font-size: var(--font-size-lg);
  font-weight: 700;
  margin: 0 0 var(--spacing-1) 0;
  color: var(--text-primary);
}

.method-info p {
  font-size: var(--font-size-sm);
  color: var(--text-secondary);
  margin: 0;
}

.method-badge {
  position: absolute;
  top: var(--spacing-3);
  right: var(--spacing-3);
  background: var(--success-500);
  color: white;
  padding: var(--spacing-1) var(--spacing-2);
  border-radius: var(--border-radius-full);
  font-size: var(--font-size-xs);
  font-weight: 700;
  text-transform: uppercase;
}

.method-status {
  display: flex;
  align-items: center;
  gap: var(--spacing-1);
  font-size: var(--font-size-xs);
  font-weight: 600;
  margin-top: var(--spacing-3);
}

.method-status.verified {
  color: var(--success-600);
}

/* Form Enhancements */
.deposit-form-container,
.withdraw-form-container {
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-xl);
  padding: var(--spacing-8);
}

.amount-input-group {
  position: relative;
  display: flex;
  align-items: center;
}

.currency-symbol {
  position: absolute;
  left: var(--spacing-4);
  font-size: var(--font-size-lg);
  font-weight: 700;
  color: var(--text-tertiary);
  z-index: 1;
}

.amount-input-group input {
  padding-left: var(--spacing-8);
  font-size: var(--font-size-xl);
  font-weight: 700;
  text-align: center;
}

.amount-note {
  margin-top: var(--spacing-2);
  font-size: var(--font-size-xs);
  color: var(--text-tertiary);
  text-align: center;
}

/* Quick Amounts */
.quick-amounts {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: var(--spacing-2);
  margin: var(--spacing-4) 0;
}

.quick-amount-btn {
  background: var(--bg-tertiary);
  border: 1px solid var(--border-secondary);
  padding: var(--spacing-3);
  border-radius: var(--border-radius-lg);
  font-weight: 600;
  color: var(--text-primary);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.quick-amount-btn:hover {
  background: var(--primary-100);
  border-color: var(--primary-300);
  color: var(--primary-700);
  transform: scale(1.05);
}

[data-theme="dark"] .quick-amount-btn:hover {
  background: rgba(33, 150, 243, 0.1);
  border-color: var(--primary-500);
  color: var(--primary-400);
}

.quick-amount-btn.max-btn {
  background: linear-gradient(135deg, var(--warning-500) 0%, var(--warning-700) 100%);
  color: white;
  border-color: var(--warning-500);
}

.quick-amount-btn.max-btn:hover {
  background: linear-gradient(135deg, var(--warning-600) 0%, var(--warning-800) 100%);
  color: white;
}

/* Fee Summary */
.fee-summary {
  background: var(--bg-tertiary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
  margin: var(--spacing-6) 0;
  border: 1px solid var(--border-secondary);
}

.fee-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-2);
  font-size: var(--font-size-sm);
}

.fee-item:last-child {
  margin-bottom: 0;
}

.fee-item.total {
  font-weight: 700;
  font-size: var(--font-size-base);
  padding-top: var(--spacing-3);
  border-top: 1px solid var(--border-primary);
  margin-top: var(--spacing-3);
  color: var(--primary-600);
}

/* Payment Details */
.payment-details,
.withdrawal-details {
  margin: var(--spacing-6) 0;
  padding: var(--spacing-4);
  background: var(--bg-tertiary);
  border-radius: var(--border-radius-lg);
  border: 1px solid var(--border-secondary);
}

.form-row {
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: var(--spacing-4);
}

.bank-info {
  background: var(--surface);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
}

.bank-info h4 {
  margin: 0 0 var(--spacing-4) 0;
  color: var(--text-primary);
  font-weight: 600;
}

.bank-detail {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-2);
  font-size: var(--font-size-sm);
}

.bank-detail:last-child {
  margin-bottom: 0;
}

.bank-detail .label {
  color: var(--text-tertiary);
  font-weight: 500;
}

.bank-detail .value {
  color: var(--text-primary);
  font-weight: 700;
  font-feature-settings: 'tnum';
}

/* Crypto Address */
.crypto-address {
  margin-top: var(--spacing-4);
}

.address-display {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-3);
  margin: var(--spacing-2) 0;
}

.address {
  flex: 1;
  font-family: 'Courier New', monospace;
  font-size: var(--font-size-sm);
  color: var(--text-primary);
  word-break: break-all;
}

.copy-btn {
  background: var(--primary-500);
  border: none;
  color: white;
  padding: var(--spacing-2);
  border-radius: var(--border-radius-md);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.copy-btn:hover {
  background: var(--primary-600);
  transform: scale(1.1);
}

.qr-code {
  margin-top: var(--spacing-4);
  text-align: center;
}

.qr-placeholder {
  width: 120px;
  height: 120px;
  background: var(--bg-tertiary);
  border: 2px dashed var(--border-primary);
  border-radius: var(--border-radius-lg);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 0 auto;
  color: var(--text-tertiary);
}

.qr-placeholder i {
  font-size: var(--font-size-2xl);
  margin-bottom: var(--spacing-2);
}

/* Account Display */
.account-display {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: var(--surface);
  border: 1px solid var(--border-secondary);
  border-radius: var(--border-radius-lg);
  padding: var(--spacing-4);
}

.account-info {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-1);
}

.account-name {
  font-weight: 600;
  color: var(--text-primary);
}

.account-number {
  font-size: var(--font-size-sm);
  color: var(--text-tertiary);
  font-family: 'Courier New', monospace;
}

.change-btn {
  background: none;
  border: 1px solid var(--border-primary);
  padding: var(--spacing-1) var(--spacing-3);
  border-radius: var(--border-radius-md);
  font-size: var(--font-size-xs);
  font-weight: 600;
  color: var(--text-secondary);
  cursor: pointer;
  transition: all var(--transition-fast);
}

.change-btn:hover {
  background: var(--primary-500);
  border-color: var(--primary-500);
  color: white;
}

/* Notes */
.deposit-note,
.withdraw-note {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  margin-top: var(--spacing-6);
  padding: var(--spacing-4);
  background: var(--warning-100);
  border: 1px solid var(--warning-300);
  border-radius: var(--border-radius-lg);
  font-size: var(--font-size-sm);
  color: var(--warning-800);
}

[data-theme="dark"] .deposit-note,
[data-theme="dark"] .withdraw-note {
  background: rgba(255, 193, 7, 0.1);
  border-color: rgba(255, 193, 7, 0.3);
  color: var(--warning-400);
}

.deposit-note i,
.withdraw-note i {
  color: var(--warning-600);
}

[data-theme="dark"] .deposit-note i,
[data-theme="dark"] .withdraw-note i {
  color: var(--warning-400);
}

/* Back Button */
.back-btn {
  background: none;
  border: none;
  color: var(--text-secondary);
  font-size: var(--font-size-lg);
  cursor: pointer;
  padding: var(--spacing-2);
  border-radius: var(--border-radius-md);
  transition: all var(--transition-fast);
  text-decoration: none;
  display: flex;
  align-items: center;
  justify-content: center;
}

.back-btn:hover {
  background: var(--bg-hover);
  color: var(--text-primary);
  transform: translateX(-2px);
}
